import xml.etree.ElementTree as ET
import json

def puzzle_analyzer(map_xml_string):
    root = ET.fromstring(map_xml_string)
    map_name = root.get('name')

    puzzle_elements = {
        "switches": [],
        "gates": []
    }

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')

            # Find gates
            if 'gate' in tile_type:
                puzzle_elements["gates"].append({
                    "coordinate": f"({x}, {y})",
                    "status": tile_type
                })

            # Find switches
            # This looks for any object associated with the switch script name
            switch_obj = tile_elem.find("./Object[@name='Mansion4Script_Switches']")
            if switch_obj is not None:
                puzzle_elements["switches"].append({
                    "coordinate": f"({x}, {y})"
                })

    print(json.dumps({map_name: puzzle_elements}, indent=2))

puzzle_analyzer(map_xml_string)
