import json
import heapq
import xml.etree.ElementTree as ET

def find_path_astar(map_xml_string, start_x, start_y, end_x, end_y, temp_impassable_coords_str=None):
    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))
    tiles = {}
    for row in root.findall('Row'):
        row_id = int(row.get('id'))
        for tile_elem in row.findall('Tile'):
            tile_id = int(tile_elem.get('id'))
            x, y = tile_id, row_id
            tile_type = tile_elem.get('type')
            has_object = tile_elem.get('has-object') == 'true'
            object_name = None
            object_id = None
            for obj_elem in tile_elem.findall('Object'):
                object_name = obj_elem.get('name') or obj_elem.get('id-name')
                object_id = obj_elem.get('id')
                break
            tiles[(x, y)] = {'type': tile_type, 'has_object': has_object, 'object_name': object_name, 'object_id': object_id}
    temp_impassable_coords = set()
    if temp_impassable_coords_str:
        try:
            temp_list = json.loads(temp_impassable_coords_str)
            for coord_pair in temp_list:
                if isinstance(coord_pair, list) and len(coord_pair) == 2:
                    temp_impassable_coords.add(tuple(coord_pair))
        except json.JSONDecodeError:
            pass
    start = (int(start_x), int(start_y))
    end_goal = (int(end_x), int(end_y))
    traversable_tiles = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'ladder_up', 'ladder_down'}

    def is_traversable(x, y, ignore_ledge=False):
        if not (1 <= x <= map_width and 1 <= y <= map_height):
            return False, 'Map boundary'
        tile_info = tiles.get((x, y))
        if not tile_info:
            return False, 'Unknown tile'
        tile_type = tile_info['type']
        has_object = tile_info['has_object']
        object_name = tile_info['object_name']
        object_id = tile_info['object_id']
        if (x, y) in temp_impassable_coords:
            return False, f'Temp impassable tile at ({x}, {y})'
        impassable_types = ['impassable', 'boulder_barrier', 'closed_gate', 'cuttable']
        if not ignore_ledge:
            impassable_types.append('ledge')
        if tile_type == 'water':
            return False, f'Water at ({x}, {y}) requires Surf'
        if tile_type in impassable_types:
            return False, f'{tile_type.replace("_", " ").title()} at ({x}, {y})'
        if has_object and object_id != '15':
            return False, f'Object: {object_name} at ({x}, {y})'
        if tile_type in traversable_tiles:
            return True, None
        return False, f'Unhandled terrain: {tile_type} at ({x}, {y})'

    goal_set = set()
    is_end_traversable, reason = is_traversable(end_goal[0], end_goal[1])
    if is_end_traversable:
        goal_set.add(end_goal)
    else:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj = (end_goal[0] + dx, end_goal[1] + dy)
            if is_traversable(adj[0], adj[1])[0]:
                goal_set.add(adj)
    if not goal_set:
        print(json.dumps({'path_found': False, 'error': f'Destination ({end_goal[0]}, {end_goal[1]}) is impassable and has no traversable neighbors. Reason: {reason}'}))
        return
    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = {start: 0}
    f_score = {start: min(abs(start[0] - g[0]) + abs(start[1] - g[1]) for g in goal_set)}
    closed_set = set()
    blocking_obstacles_on_frontier = set()
    while open_set:
        _, current = heapq.heappop(open_set)
        if current in goal_set:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start)
            print(json.dumps({'path_found': True, 'path': path[::-1]}))
            return
        closed_set.add(current)
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            if not (1 <= neighbor[0] <= map_width and 1 <= neighbor[1] <= map_height):
                continue
            if neighbor in closed_set:
                continue
            is_ledge_jump = False
            neighbor_tile_info = tiles.get(neighbor)
            if neighbor_tile_info and neighbor_tile_info['type'] == 'ledge':
                if dy == 1:
                    is_ledge_jump = True
            is_neighbor_traversable, blocking_reason = is_traversable(neighbor[0], neighbor[1], ignore_ledge=is_ledge_jump)
            if not is_neighbor_traversable:
                if blocking_reason:
                    blocking_obstacles_on_frontier.add(blocking_reason)
                continue
            path_neighbor = neighbor
            if is_ledge_jump:
                path_neighbor = (neighbor[0], neighbor[1] + 1)
                if not (1 <= path_neighbor[0] <= map_width and 1 <= path_neighbor[1] <= map_height):
                    continue
                is_landing_traversable, landing_reason = is_traversable(path_neighbor[0], path_neighbor[1])
                if not is_landing_traversable:
                    if landing_reason:
                        blocking_obstacles_on_frontier.add(f"Ledge jump blocked at landing ({path_neighbor[0]}, {path_neighbor[1]}): {landing_reason}")
                    continue
            if path_neighbor in closed_set:
                continue
            tentative_g_score = g_score.get(current, float('inf')) + 1
            if tentative_g_score < g_score.get(path_neighbor, float('inf')):
                came_from[path_neighbor] = current
                g_score[path_neighbor] = tentative_g_score
                # Heuristic calculation with tie-breaker
                h_min = min(abs(path_neighbor[0] - g[0]) + abs(path_neighbor[1] - g[1]) for g in goal_set)
                dx1 = current[0] - end_goal[0]
                dy1 = current[1] - end_goal[1]
                dx2 = start[0] - end_goal[0]
                dy2 = start[1] - end_goal[1]
                cross_product = abs(dx1 * dy2 - dx2 * dy1)
                heuristic = h_min + cross_product * 0.001 # Add small tie-breaker
                f_score[path_neighbor] = tentative_g_score + heuristic
                heapq.heappush(open_set, (f_score[path_neighbor], path_neighbor))
    error_msg = 'No path found. The destination may be in a disconnected area of the map.'
    if blocking_obstacles_on_frontier:
        obstacle_list = sorted(list(blocking_obstacles_on_frontier))
        if len(obstacle_list) > 10:
            error_msg = f"Path blocked by: {', '.join(obstacle_list[:10])} and {len(obstacle_list) - 10} more..."
        else:
            error_msg = f"Path blocked by: {', '.join(obstacle_list)}"
    print(json.dumps({'path_found': False, 'error': error_msg}))

find_path_astar(map_xml_string, input_data['start_x'], input_data['start_y'], input_data['end_x'], input_data['end_y'], input_data.get('temp_impassable'))

