import json
import re
def navigate_pc(target_pokemon_name, screen_text):
    lines = screen_text.strip().split('\n')
    pokemon_list = []
    pokemon_pattern = re.compile(r'‚Üí?([A-Z‚ôÄ‚ôÇ‚Äô]+)')
    for line in lines:
        match = pokemon_pattern.search(line)
        if match:
            pokemon_name = match.group(1).strip()
            if pokemon_name not in ['LV', 'BOX']:
                pokemon_list.append(pokemon_name)

    if not pokemon_list:
        print(json.dumps([]))
        return

    try:
        target_index = pokemon_list.index(target_pokemon_name.upper())
    except ValueError:
        print(json.dumps([]))
        return

    cursor_pos = 0
    for i, name in enumerate(pokemon_list):
        if screen_text.find(f'‚Üí{name}') != -1:
            cursor_pos = i
            break

    down_presses = (target_index - cursor_pos + len(pokemon_list)) % len(pokemon_list)
    up_presses = (cursor_pos - target_index + len(pokemon_list)) % len(pokemon_list)

    if down_presses <= up_presses:
        buttons = ['Down'] * down_presses
    else:
        buttons = ['Up'] * up_presses

    print(json.dumps(buttons))

navigate_pc(input_data['target_pokemon'], input_data['screen_text'])

