import json
import heapq
import xml.etree.ElementTree as ET

def find_ledge_path(map_xml_string, start_x, start_y, end_x, end_y):
    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))

    tiles = {}
    for row in root.findall('Row'):
        row_id = int(row.get('id'))
        for tile_elem in row.findall('Tile'):
            tile_id = int(tile_elem.get('id'))
            x, y = tile_id, row_id
            tile_type = tile_elem.get('type')
            has_object = tile_elem.find('Object') is not None
            tiles[(x, y)] = {'type': tile_type, 'has_object': has_object}

    start = (int(start_x), int(start_y))
    end = (int(end_x), int(end_y))

    traversable_types = {'ground', 'grass'}

    def get_neighbors(x, y):
        neighbors = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if not (1 <= nx <= map_width and 1 <= ny <= map_height):
                continue

            current_tile = tiles.get((x, y))
            neighbor_tile = tiles.get((nx, ny))
            if not neighbor_tile:
                continue

            # Standard movement
            if neighbor_tile['type'] in traversable_types and not neighbor_tile['has_object']:
                neighbors.append((nx, ny))

            # Ledge jump logic
            if current_tile['type'] in traversable_types and neighbor_tile['type'] == 'ledge' and dy == 1:
                landing_x, landing_y = nx, ny + 1
                if 1 <= landing_y <= map_height:
                    landing_tile = tiles.get((landing_x, landing_y))
                    if landing_tile and landing_tile['type'] in traversable_types and not landing_tile['has_object']:
                        neighbors.append((landing_x, landing_y))
        return neighbors

    queue = [(0, start, [])]
    visited = {start}

    while queue:
        cost, current, path = heapq.heappop(queue)
        path = path + [current]

        if current == end:
            print(json.dumps({'path_found': True, 'path': path}))
            return

        for neighbor in get_neighbors(current[0], current[1]):
            if neighbor not in visited:
                visited.add(neighbor)
                new_cost = len(path)
                heapq.heappush(queue, (new_cost, neighbor, path))

    print(json.dumps({'path_found': False, 'error': 'No path found.'}))

find_ledge_path(
    map_xml_string,
    input_data['start_x'],
    input_data['start_y'],
    input_data['end_x'],
    input_data['end_y']
)
