import collections
import json
import xml.etree.ElementTree as ET

def solve():
    start_x, start_y = int(input_data['start_x']), int(input_data['start_y'])
    end_x, end_y = int(input_data['end_x']), int(input_data['end_y'])

    root = ET.fromstring(map_xml_string)
    width, height = int(root.get('width')), int(root.get('height'))
    grid = [[0]*width for _ in range(height)]
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')
            has_obj = tile.get('has-object') == "true"

            if (x, y) == (end_x, end_y):
                continue

            # Obstacles: WALL, PIT, LADDER, and any Map Object (NPC/Boulder)
            if t_type in ['WALL', 'PIT', 'LADDER'] or has_obj:
                grid[y][x] = 1

    q = collections.deque([(start_x, start_y, [])])
    v = {(start_x, start_y)}
    while q:
        cx, cy, path = q.popleft()
        if (cx, cy) == (end_x, end_y):
            print(json.dumps(path))
            return
        for dx, dy, btn in [(0, 1, "Down"), (0, -1, "Up"), (1, 0, "Right"), (-1, 0, "Left")]:
            nx, ny = cx + dx, cy + dy
            if 0 <= nx < width and 0 <= ny < height and grid[ny][nx] == 0:
                if (nx, ny) not in v:
                    v.add((nx, ny))
                    q.append((nx, ny, path + [btn]))
    print("[]")

solve()

