import json
import xml.etree.ElementTree as ET
from collections import deque

def solve():
    try:
        root = ET.fromstring(map_xml_string)
        width, height = int(root.get('width')), int(root.get('height'))
        passable, pits, boulders = set(), [(2, 5), (8, 3), (8, 7)], {6: (3, 3), 7: (6, 1), 8: (8, 14)}
        for row in root.findall('Row'):
            y = int(row.get('id'))
            for tile in row.findall('Tile'):
                x = int(tile.get('id'))
                if tile.get('type') in ['FLOOR', 'LADDER', 'ICE'] and (tile.get('has-object') != 'true' or (tile.find('Object') is not None and tile.find('Object').get('id-name') == 'BOULDER')):
                    passable.add((x, y))
        extra = {(t['x'], t['y']) for t in input_data['test_tiles']}
        temp_passable = passable | extra
        results = {}
        for bid, bstart in boulders.items():
            q, v = deque([bstart]), {bstart}
            reachable_pit = None
            while q:
                bx, by = q.popleft()
                if (bx, by) in pits: reachable_pit = (bx, by); break
                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    nbx, nby, px, py = bx + dx, by + dy, bx - dx, by - dy
                    if 0 <= nbx < width and 0 <= nby < height and 0 <= px < width and 0 <= py < height and ((nbx, nby) in temp_passable or (nbx, nby) in pits) and (px, py) in temp_passable:
                        if (nbx, nby) not in v:
                            v.add((nbx, nby))
                            q.append((nbx, nby))
            results[bid] = str(reachable_pit) if reachable_pit else None
        print(json.dumps(results))
    except Exception as e:
        print(json.dumps({'error': str(e)}))
solve()
