import xml.etree.ElementTree as ET
import json

root = ET.fromstring(map_xml_string)
puzzle_elements = {'boulders': [], 'switches': [], 'barriers': []}
for row_elem in root.findall('Row'):
    y = int(row_elem.get('id'))
    for tile_elem in row_elem.findall('Tile'):
        x = int(tile_elem.get('id'))
        if tile_elem.find('Boulder') is not None:
            puzzle_elements['boulders'].append([x, y])
        if tile_elem.find('BoulderSwitch') is not None:
            puzzle_elements['switches'].append([x, y])
        if tile_elem.get('type') == 'boulder_barrier':
            puzzle_elements['barriers'].append([x, y])
print(json.dumps(puzzle_elements))
