import json
import xml.etree.ElementTree as ET
from collections import deque

def unseen_tile_checker(input_data):
    try:
        target_coords = json.loads(input_data['coordinates'])
    except json.JSONDecodeError:
        print("Error: Invalid JSON in coordinates input.")
        return

    root = ET.fromstring(map_xml_string)
    map_width = int(root.attrib['width'])
    map_height = int(root.attrib['height'])

    player_tile = root.find('.//Player/..')
    if player_tile is None:
        print("Error: Player position not found in map XML.")
        return
    start_x = int(player_tile.attrib['id'])
    start_y = int(player_tile.find('../..').attrib['id'])
    start_node = (start_x, start_y)

    traversable_types = {'FLOOR', 'TALL_GRASS', 'LONG_GRASS', 'DOOR', 'WARP_CARPET_DOWN', 'LADDER', 'CAVE', 'WARP_CARPET_LEFT', 'WARP_CARPET_RIGHT', 'LEDGE_HOP_DOWN', 'LEDGE_HOP_LEFT', 'LEDGE_HOP_RIGHT'}
    grid = {}
    for row in root.findall('Row'):
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            y = int(row.attrib['id'])
            tile_type = tile.attrib['type']
            has_object = tile.attrib.get('has-object') == 'true'
            if tile_type in traversable_types and not has_object:
                grid[(x, y)] = tile_type

    def bfs(start, end):
        queue = deque([start])
        visited = {start}
        while queue:
            current_x, current_y = queue.popleft()
            if (current_x, current_y) == end:
                return True
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                next_x, next_y = current_x + dx, current_y + dy
                neighbor = (next_x, next_y)
                if neighbor in grid and neighbor not in visited:
                    # Check one-way tile logic
                    current_tile_type = grid.get((current_x, current_y))
                    neighbor_tile_type = grid.get(neighbor)
                    can_move = True
                    if neighbor_tile_type == 'LEDGE_HOP_DOWN' and dy != 1:
                        can_move = False
                    if neighbor_tile_type == 'LEDGE_HOP_LEFT' and dx != -1:
                        can_move = False
                    if neighbor_tile_type == 'LEDGE_HOP_RIGHT' and dx != 1:
                        can_move = False

                    if can_move:
                        visited.add(neighbor)
                        queue.append(neighbor)
        return False

    reachable_tiles = []
    for coord in target_coords:
        end_node = (coord['x'], coord['y'])
        if bfs(start_node, end_node):
            reachable_tiles.append(coord)

    print(json.dumps(reachable_tiles))

unseen_tile_checker(input_data)

