pc_navigator.python_script:
import json

mode = input_data.get("mode")
buttons = []
error_msg = None

if mode == "menu_select":
    current_option = input_data.get("current_cursor_position")
    target_option = input_data.get("target_option")
    menu_options_str = input_data.get("menu_options", "[]")
    if not all([current_option, target_option, menu_options_str]):
        error_msg = "Missing arguments for menu_select mode."
    else:
        try:
            menu_options = json.loads(menu_options_str)
            current_index = menu_options.index(current_option)
            target_index = menu_options.index(target_option)
            diff = target_index - current_index
            if diff > 0:
                buttons.extend(["Down"] * diff)
            elif diff < 0:
                buttons.extend(["Up"] * abs(diff))
            buttons.append("A")
        except (ValueError, json.JSONDecodeError):
            error_msg = "Invalid current/target option or menu_options JSON."

elif mode == "box_navigate":
    current_slot_str = input_data.get("current_slot")
    target_slot_str = input_data.get("target_slot")
    if not all([current_slot_str, target_slot_str]):
        error_msg = "Missing arguments for box_navigate mode."
    else:
        try:
            current_slot = int(current_slot_str)
            target_slot = int(target_slot_str)
            current_x = (current_slot - 1) % 5
            current_y = (current_slot - 1) // 5
            target_x = (target_slot - 1) % 5
            target_y = (target_slot - 1) // 5
            x_diff = target_x - current_x
            y_diff = target_y - current_y
            if x_diff > 0: buttons.extend(["Right"] * x_diff)
            elif x_diff < 0: buttons.extend(["Left"] * abs(x_diff))
            if y_diff > 0: buttons.extend(["Down"] * y_diff)
            elif y_diff < 0: buttons.extend(["Up"] * abs(y_diff))
        except ValueError:
            error_msg = "Invalid slot number."

elif mode == "box_select":
    target_box_str = input_data.get("target_box_number")
    if not target_box_str:
        error_msg = "Missing target_box_number for box_select mode."
    else:
        try:
            target_box = int(target_box_str)
            buttons.extend(["A", "sleep 500", "A", "sleep 500"]) # Select SWITCH BOX & enter screen
            if target_box > 1:
                buttons.extend(["Right"] * (target_box - 1))
            buttons.append("A")
        except ValueError:
            error_msg = "Invalid box number."

else:
    error_msg = "Invalid mode specified."

if error_msg:
    print(json.dumps({"error": error_msg}))
else:
    print(json.dumps(buttons))

select_move_tool.python_script:
import json

current_pos = int(input_data['current_cursor_position'])
target_pos = int(input_data['target_move_slot'])

moves = []
difference = target_pos - current_pos

if difference > 0:
    for _ in range(difference):
        moves.append('Down')
elif difference < 0:
    for _ in range(abs(difference)):
        moves.append('Up')

moves.append('A')

print(json.dumps(moves))

wall_checker_tool.python_script:
import xml.etree.ElementTree as ET
import json
from collections import deque

def run_tool():
    try:
        root = ET.fromstring(map_xml_string)
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
    except (ET.ParseError, ValueError, KeyError):
        print(json.dumps(['B']))
        return

    player_pos = None
    grid = {}
    all_obstacles = set()
    all_x, all_y = [], []

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        all_y.append(y)
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            all_x.append(x)
            grid[(x, y)] = tile_elem
            if tile_elem.find('Player') is not None:
                player_pos = (x, y)
            if tile_elem.get('has-object') == 'true':
                all_obstacles.add((x, y))

    if not player_pos: 
        print(json.dumps(['B']))
        return

    min_x, max_x = min(all_x), max(all_x)
    min_y, max_y = min(all_y), max(all_y)

    impassable_tile_types = {'WALL', 'VOID', 'PC', 'TV', 'COUNTER', 'BOOKSHELF', 'WINDOW', 'unseen'}
    impassable_warps = {'DOOR', 'LADDER', 'STAIRCASE', 'PIT'}

    best_adj_tile = None
    facing_direction = None
    for dx, dy, direction in [(0, 1, 'Up'), (0, -1, 'Down'), (1, 0, 'Left'), (-1, 0, 'Right')]:
        adj_x, adj_y = target_x + dx, target_y + dy
        adj_tile = grid.get((adj_x, adj_y))
        if adj_tile and adj_tile.get('type') not in impassable_tile_types and (adj_x, adj_y) not in all_obstacles:
            best_adj_tile = (adj_x, adj_y)
            facing_direction = direction
            break

    if not best_adj_tile:
        print(json.dumps(['B']))
        return

    queue = deque([(player_pos, [])])
    visited = {player_pos}

    path_coords = None
    while queue:
        (x, y), path = queue.popleft()
        if (x, y) == best_adj_tile:
            path_coords = path + [(x, y)]
            break

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            next_x, next_y = x + dx, y + dy
            neighbor = (next_x, next_y)
            if not (min_x <= next_x <= max_x and min_y <= next_y <= max_y): continue
            if neighbor in visited: continue
            tile = grid.get(neighbor)
            if not tile: continue

            tile_type = tile.get('type')
            is_obstacle = neighbor in all_obstacles
            if tile_type in impassable_tile_types: continue
            if is_obstacle: continue
            if tile_type in impassable_warps and neighbor != best_adj_tile: continue

            visited.add(neighbor)
            new_path = path + [(x, y)]
            queue.append((neighbor, new_path))

    if path_coords and facing_direction:
        buttons = []
        current_x, current_y = player_pos
        for next_x, next_y in path_coords[1:]:
            if next_y < current_y: buttons.append('Up')
            elif next_y > current_y: buttons.append('Down')
            elif next_x < current_x: buttons.append('Left')
            elif next_x > current_x: buttons.append('Right')
            current_x, current_y = next_x, next_y
        buttons.append(facing_direction)
        buttons.append('A')
        print(json.dumps(buttons))
    else:
        print(json.dumps(['B']))

run_tool()
