import json
import xml.etree.ElementTree as ET
from collections import deque

def solve():
    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    player_x, player_y = None, None
    grid = {}
    objects = set()
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            tile_type = tile.attrib['type']
            grid[(x, y)] = tile_type
            if tile.find('Player') is not None:
                player_x, player_y = x, y
            if tile.get('has-object') == 'true':
                if tile.find('Player') is None:
                    objects.add((x, y))

    if player_x is None:
        print("Error: Player position not found.")
        return

    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    impassable_types = ['WALL', 'unseen', 'HEADBUTT_TREE', 'WATER']

    def is_traversable(x, y):
        if (x, y) in objects: return False
        t_type = grid.get((x, y), 'unseen')
        return t_type not in impassable_types

    if not (0 <= target_x < width and 0 <= target_y < height):
        print(f"Error: Target ({target_x}, {target_y}) out of bounds.")
        return

    # If target is impassable, look for adjacent
    if not is_traversable(target_x, target_y):
        targets = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = target_x + dx, target_y + dy
            if 0 <= nx < width and 0 <= ny < height and is_traversable(nx, ny):
                targets.append((nx, ny))
    else:
        targets = [(target_x, target_y)]

    if not targets:
        print(f"Error: Target ({target_x}, {target_y}) is impassable and no adjacent traversable tiles found.")
        return

    # BFS
    queue = deque([(player_x, player_y, [])])
    visited = {(player_x, player_y)}

    while queue:
        x, y, path = queue.popleft()
        if (x, y) in targets:
            print(json.dumps(path + [{'x': x, 'y': y}]))
            return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < width and 0 <= ny < height and (nx, ny) not in visited and is_traversable(nx, ny):
                visited.add((nx, ny))
                queue.append((nx, ny, path + [{'x': x, 'y': y}]))

    print(f"Error: No path found to ({target_x}, {target_y}).")

solve()
