find_path.python_script:
import xml.etree.ElementTree as ET
import json
from collections import deque

start = (input_data['start_x'], input_data['start_y'])
target = (input_data['target_x'], input_data['target_y'])
xml_data = map_xml_string

root = ET.fromstring(xml_data)
width = int(root.attrib['width'])
height = int(root.attrib['height'])

grid = {}
obstacles = set()
ledges = {}

for row in root.findall('Row'):
    y = int(row.attrib['id'])
    for tile in row.findall('Tile'):
        x = int(tile.attrib['id'])
        t_type = tile.attrib['type']
        grid[(x,y)] = t_type

        has_object = False
        for child in tile:
            if child.tag == 'Object':
                has_object = True
            if child.tag == 'Marker':
                 if child.attrib.get('object_id'):
                     has_object = True

        if t_type == 'WALL' or t_type == 'VOID':
            obstacles.add((x,y))
        elif t_type == 'LEDGE_HOP_DOWN':
            ledges[(x,y)] = 'DOWN'
            obstacles.add((x,y))
        elif has_object and (x,y) != start and (x,y) != target:
            obstacles.add((x,y))

queue = deque([(start, [])])
visited = set([start])

found_path = []

while queue:
    (cx, cy), path = queue.popleft()

    if (cx, cy) == target:
        found_path = path
        break

    moves = [
        (0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')
    ]

    for dx, dy, direction in moves:
        nx, ny = cx + dx, cy + dy

        if 0 <= nx < width and 0 <= ny < height:
            if (nx, ny) in visited:
                continue

            if (nx, ny) not in obstacles:
                visited.add((nx, ny))
                queue.append(((nx, ny), path + [direction]))
            elif direction == 'Down' and (nx, ny) in ledges and ledges[(nx, ny)] == 'DOWN':
                lx, ly = nx, ny + 1
                if 0 <= lx < width and 0 <= ly < height:
                    if (lx, ly) not in obstacles and (lx, ly) not in visited:
                         visited.add((lx, ly))
                         queue.append(((lx, ly), path + ['Down']))

print(json.dumps(found_path))

smart_battle_move.python_script:
import json

try:
    target = int(input_data.get('target_slot', 1))
    current = int(input_data.get('current_slot', 1))

    buttons = []
    # Select FIGHT
    buttons.append("A")
    buttons.append("sleep 500")

    # Navigate
    added_nav = False
    if current == 1:
        if target == 1: pass
        elif target == 2: 
            buttons.append("Down")
            added_nav = True
        elif target == 3: 
            buttons.extend(["Down", "Down"])
            added_nav = True
        elif target == 4: 
            buttons.append("Up")
            added_nav = True
    elif current == 2:
        if target == 1: 
            buttons.append("Up")
            added_nav = True
        elif target == 2: pass
        elif target == 3: 
            buttons.append("Down")
            added_nav = True
        elif target == 4: 
            buttons.extend(["Down", "Down"])
            added_nav = True
    elif current == 3:
        if target == 1: 
            buttons.extend(["Up", "Up"])
            added_nav = True
        elif target == 2: 
            buttons.append("Up")
            added_nav = True
        elif target == 3: pass
        elif target == 4: 
            buttons.append("Down")
            added_nav = True
    elif current == 4:
        if target == 1: 
            buttons.append("Down")
            added_nav = True
        elif target == 2: 
            buttons.extend(["Down", "Down"])
            added_nav = True
        elif target == 3: 
            buttons.append("Up")
            added_nav = True
        elif target == 4: pass

    # Avoid consecutive sleeps
    if added_nav:
        buttons.append("sleep 250")

    buttons.append("A") # Select Move

    print(json.dumps(buttons))
except Exception as e:
    # Fallback to simple A press if logic fails
    print(json.dumps(["A", "sleep 500", "A"]))
