import xml.etree.ElementTree as ET
import heapq
import json

def parse_map_xml(xml_string):
    root = ET.fromstring(xml_string)
    map_data = {
        'width': int(root.attrib['width']),
        'height': int(root.attrib['height']),
        'tiles': {},
        'warps': []
    }
    for row_elem in root.findall('Row'):
        y = int(row_elem.attrib['id'])
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.attrib['id'])
            tile_type = tile_elem.attrib.get('type', 'VOID')
            has_object = tile_elem.attrib.get('has-object') == 'true'
            map_data['tiles'][(x, y)] = {'type': tile_type, 'has_object': has_object}
            if tile_elem.attrib.get('is-warp') == 'true':
                # Placeholder for multi-map pathing logic
                # Currently, it only recognizes warps but doesn't path between maps.
                map_data['warps'].append({'x': x, 'y': y})
    return map_data

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def a_star_search(map_data, start, end, traversable_types):
    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = {start: 0}
    f_score = {start: heuristic(start, end)}

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start)
            return path[::-1]

        # Add neighbors
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            if neighbor not in map_data['tiles']:
                continue

            tile_info = map_data['tiles'][neighbor]
            if tile_info['type'] not in traversable_types or tile_info['has_object']:
                continue

            tentative_g_score = g_score[current] + 1
            if neighbor not in g_score or tentative_g_score < g_score[neighbor]:
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + heuristic(neighbor, end)
                heapq.heappush(open_set, (f_score[neighbor], neighbor))
    return None

