import xml.etree.ElementTree as ET
import json

target_id = input_data['object_id']
root = ET.fromstring(map_xml_string)
found = False
for row in root.findall('Row'):
    y = row.get('id')
    for tile in row.findall('Tile'):
        x = tile.get('id')
        for obj in tile.findall('Object'):
            if obj.get('id') == target_id:
                print(json.dumps({'x': x, 'y': y}))
                found = True
                break
        if found:
            break
    if found:
        break

if not found:
    print(json.dumps({'error': 'Object not found on screen.'}))

pathfinder.python_script:
import xml.etree.ElementTree as ET
import heapq
import json

def find_path(start_node, end_node, tile_data, traversable, width, height):
    open_set = []
    heapq.heappush(open_set, (0, start_node))
    came_from = {}
    g_score = {start_node: 0}

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end_node:
            path = []
            temp = current
            while temp in came_from:
                path.append(temp)
                temp = came_from[temp]
            path.append(start_node)
            path.reverse()
            return path

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)

            if not (1 <= neighbor[0] <= width and 1 <= neighbor[1] <= height):
                continue

            neighbor_info = tile_data.get(neighbor)
            if not neighbor_info or neighbor_info['has_object'] or neighbor_info['type'] not in traversable:
                continue

            current_info = tile_data.get(current)
            if current_info and current_info['type'] == 'LEDGE_HOP_DOWN' and dy == -1: continue
            if current_info and current_info['type'] == 'LEDGE_HOP_LEFT' and dx == 1: continue
            if current_info and current_info['type'] == 'LEDGE_HOP_RIGHT' and dx == -1: continue

            is_current_fuw = current_info and current_info['type'] == 'FLOOR_UP_WALL'
            is_neighbor_fuw = neighbor_info['type'] == 'FLOOR_UP_WALL'
            if is_neighbor_fuw and not is_current_fuw and dy != -1: continue
            if is_current_fuw and not is_neighbor_fuw and dy != 1: continue
            if is_current_fuw and is_neighbor_fuw and dy != 0: continue

            tentative_g_score = g_score.get(current, float('inf')) + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score = tentative_g_score + abs(neighbor[0] - end_node[0]) + abs(neighbor[1] - end_node[1])
                heapq.heappush(open_set, (f_score, neighbor))
    return None

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    base_traversable_tiles = {tile.strip() for tile in input_data.get('traversable_tiles', '').split(',')}
    hms_available = {hm.strip().upper() for hm in input_data.get('hms_available', '').split(',') if hm.strip()}

    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    tile_data = {}
    water_tiles = set()
    land_tiles = set()
    for row_elem in root.findall('Row'):
        y = int(row_elem.attrib['id'])
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.attrib['id'])
            tile_type = tile_elem.attrib['type']
            has_object = tile_elem.attrib.get('has-object') == 'true'
            tile_data[(x, y)] = {'type': tile_type, 'has_object': has_object}
            if tile_type == 'WATER' and not has_object:
                water_tiles.add((x, y))
            elif tile_type in base_traversable_tiles and not has_object:
                land_tiles.add((x, y))

    start_node = (start_x, start_y)
    target_node = (end_x, end_y)

    start_is_water = start_node in water_tiles
    target_is_water = target_node in water_tiles

    final_path = None
    if start_is_water == target_is_water:
        traversable = water_tiles if start_is_water and 'SURF' in hms_available else land_tiles
        final_path = find_path(start_node, target_node, tile_data, traversable, width, height)
    elif 'SURF' in hms_available:
        if not start_is_water and target_is_water: # Land to Water
            shortest_hybrid_path = None
            for water_entry in water_tiles:
                path_to_water = find_path(start_node, water_entry, tile_data, land_tiles, width, height)
                if path_to_water:
                    path_from_water = find_path(water_entry, target_node, tile_data, water_tiles, width, height)
                    if path_from_water:
                        hybrid_path = path_to_water + path_from_water[1:]
                        if not shortest_hybrid_path or len(hybrid_path) < len(shortest_hybrid_path):
                            shortest_hybrid_path = hybrid_path
            final_path = shortest_hybrid_path
        elif start_is_water and not target_is_water: # Water to Land
            shortest_hybrid_path = None
            for land_exit in land_tiles:
                path_to_land = find_path(start_node, land_exit, tile_data, water_tiles, width, height)
                if path_to_land:
                    path_from_land = find_path(land_exit, target_node, tile_data, land_tiles, width, height)
                    if path_from_land:
                        hybrid_path = path_to_land + path_from_land[1:]
                        if not shortest_hybrid_path or len(hybrid_path) < len(shortest_hybrid_path):
                            shortest_hybrid_path = hybrid_path
            final_path = shortest_hybrid_path

    if final_path:
        print(json.dumps({'path': [{'x': p[0], 'y': p[1]} for p in final_path]}))
    else:
        print(json.dumps({'error': 'No path found.'}))
except Exception as e:
    print(json.dumps({'error': f'An error occurred: {e}'}))

warp_scanner.python_script:
import xml.etree.ElementTree as ET
import json

root = ET.fromstring(map_xml_string)
unmarked_warps = []
for row_elem in root.findall('Row'):
    for tile_elem in row_elem.findall('Tile'):
        if tile_elem.attrib.get('is-warp') == 'true':
            has_marker = False
            for child in tile_elem:
                if child.tag == 'Marker':
                    has_marker = True
                    break
            if not has_marker:
                x = tile_elem.attrib['id']
                y = row_elem.attrib['id']
                unmarked_warps.append({'x': int(x), 'y': int(y)})
print(json.dumps({'unmarked_warps': unmarked_warps}))

puzzle_solver_step.python_script:
import json
import heapq

def parse_grid_input(grid_str):
    grid = {}
    pieces = grid_str.replace('[', '').replace(']', '').split(') ')
    for piece_str in pieces:
        if not piece_str:
            continue
        parts = piece_str.strip().split(' (')
        name_part = parts[0]
        coord_part = parts[1].replace(')', '')
        x_str, y_str = coord_part.split(',')
        x, y = int(x_str), int(y_str)
        if "Piece" in name_part:
            piece_id = int(name_part.split(' ')[1])
            grid[(x, y)] = piece_id
        else:
            grid[(x, y)] = 0
    return grid

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def a_star_search(start, goal, grid):
    neighbors = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    close_set = set()
    came_from = {}
    gscore = {start: 0}
    fscore = {start: heuristic(start, goal)}
    oheap = []
    heapq.heappush(oheap, (fscore[start], start))
    while oheap:
        current = heapq.heappop(oheap)[1]
        if current == goal:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.reverse()
            return path
        close_set.add(current)
        for i, j in neighbors:
            neighbor = current[0] + i, current[1] + j
            if 0 <= neighbor[0] < 6 and 0 <= neighbor[1] < 6:
                tentative_g_score = gscore[current] + 1
                if neighbor in close_set and tentative_g_score >= gscore.get(neighbor, 0):
                    continue
                if tentative_g_score < gscore.get(neighbor, 0) or neighbor not in [i[1] for i in oheap]:
                    came_from[neighbor] = current
                    gscore[neighbor] = tentative_g_score
                    fscore[neighbor] = tentative_g_score + heuristic(neighbor, goal)
                    heapq.heappush(oheap, (fscore[neighbor], neighbor))
    return []

def get_move(current, next_pos):
    dx = next_pos[0] - current[0]
    dy = next_pos[1] - current[1]
    if dx == 1: return "Right"
    if dx == -1: return "Left"
    if dy == 1: return "Down"
    if dy == -1: return "Up"
    return "A"

try:
    start_grid_str = input_data['start_grid']
    target_grid_str = input_data['target_grid']
    cursor_x = int(input_data['cursor_x'])
    cursor_y = int(input_data['cursor_y'])
    holding_piece = input_data['holding_piece'].lower() == 'true'
    held_piece_id_str = input_data.get('held_piece_id')
    held_piece_id = int(held_piece_id_str) if held_piece_id_str and held_piece_id_str != 'null' else None
    start_grid = parse_grid_input(start_grid_str)
    target_grid = parse_grid_input(target_grid_str)
    cursor_pos = (cursor_x, cursor_y)
    action = ""
    if not holding_piece:
        piece_to_move = -1
        current_pos = None
        for i in range(1, 17):
            current_piece_pos = next((pos for pos, pid in start_grid.items() if pid == i), None)
            target_piece_pos = next((pos for pos, pid in target_grid.items() if pid == i), None)
            if current_piece_pos != target_piece_pos:
                piece_to_move = i
                current_pos = current_piece_pos
                break
        if piece_to_move != -1:
            if cursor_pos == current_pos:
                action = "A"
            else:
                path = a_star_search(cursor_pos, current_pos, start_grid)
                if path:
                    action = get_move(cursor_pos, path[0])
                else:
                    action = "Start"
    else:
        target_pos = next((pos for pos, pid in target_grid.items() if pid == held_piece_id), None)
        if target_pos:
            if start_grid.get(target_pos, 0) != 0:
                blocking_piece_id = start_grid[target_pos]
                blocking_piece_target_pos = next((pos for pos, pid in target_grid.items() if pid == blocking_piece_id), None)
                empty_spot = next((pos for pos, pid in start_grid.items() if pid == 0 and pos != target_pos and pos != blocking_piece_target_pos), None)
                if empty_spot:
                    if cursor_pos == target_pos:
                        action = "A"
                    else:
                        path = a_star_search(cursor_pos, target_pos, start_grid)
                        if path:
                            action = get_move(cursor_pos, path[0])
                        else:
                            action = "Start"
                else:
                    action = "Start"
            else:
                if cursor_pos == target_pos:
                    action = "A"
                else:
                    path = a_star_search(cursor_pos, target_pos, start_grid)
                    if path:
                        action = get_move(cursor_pos, path[0])
                    else:
                        action = "Start"
        else:
             action = "Start"
    print(json.dumps({"action": action}))
except Exception as e:
    print(json.dumps({"action": "Start", "error": str(e)}))
