import json

def navigate_menu(directions, hold_ms=150, interval_ms=300, autopress_buttons=True):
    # Frame duration is approx 16ms
    frames_to_hold = max(1, int(hold_ms / 16))

    output_sequence = []

    for btn in directions:
        # Hold the button
        for _ in range(frames_to_hold):
            output_sequence.append(btn)

        # Release/Wait
        if interval_ms > 0:
            output_sequence.append(f"sleep {interval_ms}")

    print(json.dumps(output_sequence))
