import json

def select_move():
    try:
        # input_data is a dictionary containing the tool arguments
        move_index = int(input_data.get('move_index', 1))
        current_index = int(input_data.get('current_index', 1))

        # Calculate shortest path in a wrapping 4-item menu
        diff = (move_index - current_index) % 4
        if diff == 0:
            buttons = ["A"]
        elif diff == 1:
            buttons = ["Down", "A"]
        elif diff == 2:
            buttons = ["Down", "Down", "A"]
        elif diff == 3:
            buttons = ["Up", "A"]
        else:
            buttons = ["A"]
        print(json.dumps(buttons))
    except Exception as e:
        # Fallback to just pressing A if logic fails
        print(json.dumps(["A"]))

if __name__ == "__main__":
    select_move()
