        input_data = json.loads(sys.argv[1])
        start_pos = (int(input_data['start_x']), int(input_data['start_y']))
        target_pos = (int(input_data['target_x']), int(input_data['target_y']))

        root = ET.fromstring(map_xml_string)
        grid = {}
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                x = int(tile.attrib['id'])
                t_type = tile.attrib['type']
                has_obj = tile.attrib.get('has-object') == 'true'
                if t_type in ['WALL', 'BUOY', 'HEADBUTT_TREE', 'CUT_TREE', 'FRUIT_TREE', 'COUNTER', 'WATER'] or has_obj:
                    grid[(x, y)] = False
                elif t_type == 'FLOOR_UP_WALL':
                    grid[(x, y)] = 'UP_WALL'
                elif t_type == 'LEDGE_HOP_DOWN':
                    grid[(x, y)] = 'LEDGE'
                else:
                    grid[(x, y)] = True

        queue = deque([(start_pos, [])])
        visited = {start_pos}
        while queue:
            (curr_x, curr_y), path = queue.popleft()
            if (curr_x, curr_y) == target_pos: 
                print(json.dumps([{'x': p[0], 'y': p[1]} for p in path + [(curr_x, curr_y)]]))
                return
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = curr_x + dx, curr_y + dy
                if (nx, ny) in grid:
                    if grid[(nx, ny)] == False: continue
                    if dy == 1 and grid[(nx, ny)] == 'UP_WALL': continue
                    if dy == -1 and grid[(curr_x, curr_y)] == 'UP_WALL': continue
                    if dy == -1 and grid[(nx, ny)] == 'LEDGE': continue
                    if (nx, ny) not in visited:
                        visited.add((nx, ny))
                        queue.append(((nx, ny), path + [(curr_x, curr_y)]))
        print('[]')
    except Exception as e:
        print(json.dumps({'error': str(e)}))

