import xml.etree.ElementTree as ET
import json
import collections

def find_reachable_unseen_tiles(map_xml_string, start_x_str, start_y_str):
    start_x = int(start_x_str)
    start_y = int(start_y_str)

    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))

    grid = [[None for _ in range(map_width + 1)] for _ in range(map_height + 1)]
    obstacles = set()

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            seen = tile_elem.get('seen') == 'true'
            grid[y][x] = {'type': tile_type, 'seen': seen}
            if tile_elem.find('Object') is not None:
                obstacles.add((x, y))

    traversable_tiles = {
        "FLOOR", "TALL_GRASS", "DOOR", "STAIRCASE", "WARP_CARPET_DOWN",
        "LEDGE_HOP_DOWN", "LEDGE_HOP_LEFT", "LEDGE_HOP_RIGHT"
    }

    queue = collections.deque([(start_x, start_y)])
    visited = set([(start_x, start_y)])
    reachable_tiles = set([(start_x, start_y)])

    while queue:
        current_x, current_y = queue.popleft()

        for dx, dy, direction in [(0, -1, 'up'), (0, 1, 'down'), (-1, 0, 'left'), (1, 0, 'right')]:
            next_x, next_y = current_x + dx, current_y + dy

            if not (1 <= next_x <= map_width and 1 <= next_y <= map_height):
                continue

            if (next_x, next_y) in visited:
                continue

            current_tile_info = grid[current_y][current_x]
            next_tile_info = grid[next_y][next_x]

            if next_tile_info is None or current_tile_info is None:
                continue

            current_tile_type = current_tile_info['type']
            next_tile_type = next_tile_info['type']

            if next_tile_type not in traversable_tiles or (next_x, next_y) in obstacles:
                continue

            is_moving_to_ledge = next_tile_type.startswith("LEDGE_HOP")
            is_moving_from_ledge = current_tile_type.startswith("LEDGE_HOP")

            if is_moving_to_ledge and not is_moving_from_ledge:
                if next_tile_type == "LEDGE_HOP_DOWN" and direction != 'down':
                    continue
                if next_tile_type == "LEDGE_HOP_LEFT" and direction != 'left':
                    continue
                if next_tile_type == "LEDGE_HOP_RIGHT" and direction != 'right':
                    continue

            if is_moving_to_ledge and is_moving_from_ledge and current_tile_type != next_tile_type:
                continue

            visited.add((next_x, next_y))
            queue.append((next_x, next_y))
            reachable_tiles.add((next_x, next_y))

    reachable_unseen_tiles = []
    for y in range(1, map_height + 1):
        for x in range(1, map_width + 1):
            tile_info = grid[y][x]
            if tile_info and not tile_info['seen']:
                is_adjacent_to_reachable = False
                for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    neighbor_x, neighbor_y = x + dx, y + dy
                    if (neighbor_x, neighbor_y) in reachable_tiles:
                        is_adjacent_to_reachable = True
                        break
                if is_adjacent_to_reachable:
                     reachable_unseen_tiles.append({'x': x, 'y': y})

    print(json.dumps(reachable_unseen_tiles))

find_reachable_unseen_tiles(map_xml_string, input_data["start_x"], input_data["start_y"])
