import xml.etree.ElementTree as ET
import heapq
import json

def find_path(map_xml_string, target_x, target_y, party_data):
    root = ET.fromstring(map_xml_string)
    player_pos = None
    for row in root.findall('Row'):
        for tile in row.findall('Tile'):
            if tile.find('Player') is not None:
                player_pos = (int(tile.get('id')), int(row.get('id')))
                break
        if player_pos:
            break

    if not player_pos:
        return 'Player not found on the map.'

    start_x, start_y = player_pos

    def has_hm(party, hm_name):
        for pokemon in party:
            if pokemon.get('is_fainted', True):
                continue
            if pokemon.get('moves') and any(move.get('name') == hm_name for move in pokemon.get('moves', [])):
                return True
        return False

    parsed_party = json.loads(party_data)

    def get_tile(x, y):
        tile_element = root.find(f".//Row[@id='{y}']/Tile[@id='{x}']")
        return tile_element

    def is_traversable(x, y, from_x, from_y):
        tile = get_tile(x, y)
        if tile is None:
            return False

        tile_type = tile.get('type')
        has_object = tile.find('Object') is not None and tile.find('Object').get('id-name') != 'PIKACHU'

        if has_object:
            return False

        if tile_type in ['impassable', 'boulder_barrier', 'unknown', 'closed_gate']:
            return False

        if tile_type == 'cuttable':
            return has_hm(parsed_party, 'CUT')

        if tile_type == 'water':
            from_tile = get_tile(from_x, from_y)
            from_tile_type = from_tile.get('type') if from_tile is not None else ''
            if from_tile_type == 'water':
                return True
            # Corrected logic: Can only start surfing from a 'ground' tile.
            if from_tile_type == 'ground' and has_hm(parsed_party, 'SURF'):
                return True
            return False

        if tile_type == 'ledge':
            return from_y < y

        return True

    def get_neighbors(x, y):
        neighbors = []
        for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = x + dx, y + dy
            current_tile = get_tile(x,y)
            current_tile_type = current_tile.get('type') if current_tile is not None else ''

            if is_traversable(nx, ny, x, y):
                cost = 1
                neighbor_tile = get_tile(nx,ny)
                neighbor_tile_type = neighbor_tile.get('type') if neighbor_tile is not None else ''

                if neighbor_tile_type == 'ledge' and dy == 1:
                    ny += 1 # Auto-jump down
                elif neighbor_tile_type == 'water' and current_tile_type != 'water':
                    cost += 1 # cost to start surfing

                neighbors.append(((nx, ny), move, cost))
        return neighbors

    target_tile = get_tile(target_x, target_y)
    if target_tile is None or not is_traversable(target_x, target_y, start_x, start_y):
        found_new_target = False
        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            adj_x, adj_y = target_x + dx, target_y + dy
            if is_traversable(adj_x, adj_y, start_x, start_y):
                target_x, target_y = adj_x, adj_y
                found_new_target = True
                break
        if not found_new_target:
            return 'Target is impassable and no adjacent traversable tile found.'

    open_set = [(0, (start_x, start_y), [])]
    closed_set = set()

    while open_set:
        cost, current, path = heapq.heappop(open_set)

        if current == (target_x, target_y):
            return path

        if current in closed_set:
            continue

        closed_set.add(current)

        for neighbor, move, move_cost in get_neighbors(current[0], current[1]):
            if neighbor in closed_set:
                continue

            new_cost = cost + move_cost
            new_path = path + [move]
            heapq.heappush(open_set, (new_cost, neighbor, new_path))

    return 'No path found.'

print(find_path(map_xml_string, int(input_data['target_x']), int(input_data['target_y']), input_data['party_data']))

