import xml.etree.ElementTree as ET
from collections import deque
import json

def get_map_grid(map_data):
    width = int(map_data.get("width"))
    height = int(map_data.get("height"))
    grid = [[None for _ in range(width)] for _ in range(height)]
    player_pos = None

    for row in map_data.findall("Row"):
        y = int(row.get("id"))
        for tile in row.findall("Tile"):
            x = int(tile.get("id"))
            grid[y][x] = tile
            if tile.find("Player") is not None:
                player_pos = (x, y)
    return grid, width, height, player_pos

def is_tile_blocked(tile):
    if tile is None: return True

    # Check for explicit object
    if tile.get("has-object") == "true":
        return True

    # Check for marker with object_id (implies object is there even if off-screen)
    for marker in tile.findall("Marker"):
        if marker.get("object_id"):
            return True

    return False

def get_neighbors(cx, cy, grid, width, height):
    neighbors = []
    moves = [(0, -1), (0, 1), (-1, 0), (1, 0)]

    for dx, dy in moves:
        nx, ny = cx + dx, cy + dy

        if 0 <= nx < width and 0 <= ny < height:
            next_tile = grid[ny][nx]
            if next_tile is None: continue

            next_type = next_tile.get("type")

            # Check blockage (objects/markers)
            if is_tile_blocked(next_tile):
                continue

            can_enter = False
            basic_types = ["FLOOR", "TALL_GRASS", "LONG_GRASS", "STAIRS", 
                           "WARP_CARPET_DOWN", "WARP_CARPET_UP", "WARP_CARPET_LEFT", "WARP_CARPET_RIGHT",
                           "DOOR", "OPEN_DOOR", "LADDER", "CAVE"]

            if next_type in basic_types:
                can_enter = True
            elif next_type == "LEDGE_HOP_DOWN" and dy == 1:
                can_enter = True
            elif next_type == "LEDGE_HOP_LEFT" and dx == -1:
                can_enter = True
            elif next_type == "LEDGE_HOP_RIGHT" and dx == 1:
                can_enter = True
            elif next_type == "FLOOR_UP_WALL" and (dy == -1 or dy == 0):
                can_enter = True

            if can_enter:
                neighbors.append((nx, ny))

    return neighbors

def solve_path(start, target, grid, width, height):
    target_tile = grid[target[1]][target[0]]

    # Determine if target is strictly reachable or if we need to path to neighbor
    target_blocked = False
    if target_tile is not None:
        basic_types = ["FLOOR", "TALL_GRASS", "LONG_GRASS", "STAIRS", "WARP_CARPET_DOWN", "WARP_CARPET_UP", "WARP_CARPET_LEFT", "WARP_CARPET_RIGHT", "DOOR", "OPEN_DOOR", "LADDER", "CAVE"]
        if target_tile.get("type") not in basic_types:
            target_blocked = True
        if is_tile_blocked(target_tile):
            target_blocked = True
    else:
        target_blocked = True

    valid_destinations = set()
    if target_blocked:
        # If target blocked, try neighbors
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = target[0] + dx, target[1] + dy
            if 0 <= nx < width and 0 <= ny < height:
                tile = grid[ny][nx]
                if tile is not None:
                     t_type = tile.get("type")
                     basic_types = ["FLOOR", "TALL_GRASS", "LONG_GRASS", "STAIRS", "WARP_CARPET_DOWN", "WARP_CARPET_UP", "WARP_CARPET_LEFT", "WARP_CARPET_RIGHT", "DOOR", "OPEN_DOOR", "LADDER", "CAVE"]
                     if t_type in basic_types and not is_tile_blocked(tile):
                         valid_destinations.add((nx, ny))
    else:
        valid_destinations.add(target)

    if not valid_destinations:
        return None

    queue = deque([(start, [start])])
    visited = set([start])

    while queue:
        current_pos, path = queue.popleft()
        cx, cy = current_pos

        if current_pos in valid_destinations:
            return path

        for nx, ny in get_neighbors(cx, cy, grid, width, height):
            if (nx, ny) not in visited:
                visited.add((nx, ny))
                queue.append(((nx, ny), path + [(nx, ny)]))

    return None

