import json
import xml.etree.ElementTree as ET
from collections import deque

def solve_boulder_puzzle(map_xml_string):
    root = ET.fromstring(map_xml_string)
    grid = {}
    all_boulders = []
    holes = []
    player_start = None
    pikachu_pos = None

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id')) - 1
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id')) - 1
            tile_type = tile_elem.get('type')
            grid[(x, y)] = tile_type
            if tile_elem.find('Boulder') is not None:
                all_boulders.append((x, y))
            if tile_type == 'hole':
                holes.append((x, y))
            if tile_elem.find('Player') is not None:
                player_start = (x, y)
            obj_elem = tile_elem.find('Object')
            if obj_elem is not None and 'Pikachu' in obj_elem.get('id-name', ''):
                pikachu_pos = (x, y)

    traversable = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'hole'}
    initial_boulders = []
    for b in all_boulders:
        if grid.get(b) != 'water':
            is_movable = False
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                player_pos_check = (b[0] - dx, b[1] - dy)
                push_to_pos = (b[0] + dx, b[1] + dy)
                if grid.get(player_pos_check) in traversable and grid.get(push_to_pos) in traversable:
                    is_movable = True
                    break
            if is_movable:
                initial_boulders.append(b)

    initial_boulders.sort()
    initial_state = (player_start, tuple(initial_boulders))

    queue = deque([(initial_state, [])])
    visited = {initial_state}

    while queue:
        (current_player, current_boulders_tuple), path = queue.popleft()
        current_boulders = set(current_boulders_tuple)

        if not set(holes).difference(current_boulders):
            solution_path = []
            for action in path:
                if action['type'] == 'move':
                    solution_path.append({'type': 'move', 'start': (action['start'][0] + 1, action['start'][1] + 1), 'end': (action['end'][0] + 1, action['end'][1] + 1)})
                elif action['type'] == 'push':
                    solution_path.append({'type': 'push', 'player_pos': (action['player_pos'][0] + 1, action['player_pos'][1] + 1), 'boulder_start': (action['boulder_start'][0] + 1, action['boulder_start'][1] + 1), 'boulder_end': (action['boulder_end'][0] + 1, action['boulder_end'][1] + 1)})
            return json.dumps({'solution': solution_path, 'status': 'success'})

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            next_player_pos = (current_player[0] + dx, current_player[1] + dy)

            is_pikachu_tile = (next_player_pos == pikachu_pos)
            if (grid.get(next_player_pos) in traversable or is_pikachu_tile) and next_player_pos not in current_boulders:
                new_state = (next_player_pos, current_boulders_tuple)
                if new_state not in visited:
                    visited.add(new_state)
                    new_path = path + [{'type': 'move', 'start': current_player, 'end': next_player_pos}]
                    queue.append((new_state, new_path))

            elif next_player_pos in current_boulders:
                boulder_to_push = next_player_pos
                next_boulder_pos = (boulder_to_push[0] + dx, boulder_to_push[1] + dy)
                if grid.get(next_boulder_pos) in traversable and next_boulder_pos not in current_boulders and next_boulder_pos != pikachu_pos:
                    new_boulders = list(current_boulders_tuple)
                    new_boulders.remove(boulder_to_push)
                    new_boulders.append(next_boulder_pos)
                    new_boulders.sort()
                    new_boulders_tuple = tuple(new_boulders)

                    new_player_pos = current_player
                    new_state = (new_player_pos, new_boulders_tuple)

                    if new_state not in visited:
                        visited.add(new_state)
                        new_path = path + [{'type': 'push', 'player_pos': current_player, 'boulder_start': boulder_to_push, 'boulder_end': next_boulder_pos}]
                        queue.append((new_state, new_path))

    return json.dumps({'solution': [], 'status': 'failure', 'reason': 'No solution found'})

result = solve_boulder_puzzle(map_xml_string)
print(result)

