import xml.etree.ElementTree as ET
import json

target_id = input_data['object_id']
root = ET.fromstring(map_xml_string)
found = False
for row in root.findall('Row'):
    y = row.get('id')
    for tile in row.findall('Tile'):
        x = tile.get('id')
        for obj in tile.findall('Object'):
            if obj.get('id') == target_id:
                print(json.dumps({'x': x, 'y': y}))
                found = True
                break
        if found:
            break
    if found:
        break

if not found:
    print(json.dumps({'error': 'Object not found on screen.'}))
