import xml.etree.ElementTree as ET
from collections import deque
import json

def solve():
    try:
        data = input_data
        start_x = int(data['start_x'])
        start_y = int(data['start_y'])
        end_x = int(data['end_x'])
        end_y = int(data['end_y'])
    except Exception as e:
        print(f"Error parsing input: {e}")
        return

    root = ET.fromstring(map_xml_string)

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            grid[(x, y)] = {
                'type': tile.attrib['type'],
                'has_object': tile.attrib.get('has-object') == 'true'
            }

    walkable = {'FLOOR', 'LADDER', 'CAVE', 'WARP_CARPET_DOWN', 'DOOR', 'STAIRCASE', 'unseen'}

    queue = deque([((start_x, start_y), [])])
    visited = {(start_x, start_y)}

    while queue:
        (curr_x, curr_y), path = queue.popleft()

        if (curr_x, curr_y) == (end_x, end_y):
            print(json.dumps(path))
            return

        for dx, dy, move in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = curr_x + dx, curr_y + dy

            if (nx, ny) not in grid:
                continue

            target = grid[(nx, ny)]
            target_type = target['type']

            if target['has_object'] and (nx, ny) != (end_x, end_y):
                continue

            if target_type == 'FLOOR_UP_WALL' and move == 'Down':
                continue

            if target_type in walkable or (target_type == 'FLOOR_UP_WALL' and move != 'Down'):
                if (nx, ny) not in visited:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), path + [move]))
                continue

            if target_type == 'LEDGE_HOP_DOWN' and move == 'Down':
                nnx, nny = nx, ny + 1
                if (nnx, nny) in grid and (nnx, nny) not in visited:
                    landing = grid[(nnx, nny)]
                    if landing['type'] in walkable and not landing['has_object']:
                        visited.add((nnx, nny))
                        queue.append(((nnx, nny), path + [move]))
            elif target_type == 'LEDGE_HOP_LEFT' and move == 'Left':
                nnx, nny = nx - 1, ny
                if (nnx, nny) in grid and (nnx, nny) not in visited:
                    landing = grid[(nnx, nny)]
                    if landing['type'] in walkable and not landing['has_object']:
                        visited.add((nnx, nny))
                        queue.append(((nnx, nny), path + [move]))
            elif target_type == 'LEDGE_HOP_RIGHT' and move == 'Right':
                nnx, nny = nx + 1, ny
                if (nnx, nny) in grid and (nnx, nny) not in visited:
                    landing = grid[(nnx, nny)]
                    if landing['type'] in walkable and not landing['has_object']:
                        visited.add((nnx, nny))
                        queue.append(((nnx, nny), path + [move]))

    print("[]")

solve()
