import heapq
import xml.etree.ElementTree as ET

def pathfinder(input_data):
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])

    root = ET.fromstring(map_xml_string)
    map_width = int(root.attrib['width'])
    map_height = int(root.attrib['height'])

    grid = [[0 for _ in range(map_width + 1)] for _ in range(map_height + 1)]
    traversable_tiles = {'FLOOR', 'TALL_GRASS', 'LONG_GRASS', 'DOOR', 'WARP_CARPET_DOWN', 'LADDER', 'CAVE', 'WARP_CARPET_LEFT', 'WARP_CARPET_RIGHT'}
    one_way_down = {'LEDGE_HOP_DOWN'}
    one_way_left = {'LEDGE_HOP_LEFT'}
    one_way_right = {'LEDGE_HOP_RIGHT'}

    for row in root.findall('Row'):
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            y = int(row.attrib['id'])
            tile_type = tile.attrib['type']
            has_object = tile.attrib.get('has-object') == 'true'

            if tile_type in traversable_tiles and not has_object:
                grid[y][x] = 1 # Traversable
            elif tile_type in one_way_down:
                grid[y][x] = 2 # One-way down
            elif tile_type in one_way_left:
                grid[y][x] = 3 # One-way left
            elif tile_type in one_way_right:
                grid[y][x] = 4 # One-way right
            else:
                grid[y][x] = 0 # Wall

    def heuristic(a, b):
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    start = (start_x, start_y)
    end = (end_x, end_y)
    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = {start: 0}
    f_score = {start: heuristic(start, end)}

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start)
            return path[::-1]

        x, y = current
        neighbors = [(x, y - 1), (x, y + 1), (x - 1, y), (x + 1, y)]

        for neighbor_x, neighbor_y in neighbors:
            if not (1 <= neighbor_x <= map_width and 1 <= neighbor_y <= map_height):
                continue

            neighbor = (neighbor_x, neighbor_y)
            tile_type_val = grid[neighbor_y][neighbor_x]

            can_move = False
            if tile_type_val == 1: # Standard traversable
                can_move = True
            elif tile_type_val == 2: # One-way down
                if neighbor_y > y: # Only allow moving down
                    can_move = True
            elif tile_type_val == 3: # One-way left
                if neighbor_x < x: # Only allow moving left
                    can_move = True
            elif tile_type_val == 4: # One-way right
                if neighbor_x > x: # Only allow moving right
                    can_move = True

            if can_move:
                tentative_g_score = g_score[current] + 1
                if neighbor not in g_score or tentative_g_score < g_score[neighbor]:
                    came_from[neighbor] = current
                    g_score[neighbor] = tentative_g_score
                    f_score[neighbor] = tentative_g_score + heuristic(neighbor, end)
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    return None

