import json
import sys

def main():
    try:
        input_data = json.load(sys.stdin)
        sequence = input_data.get('sequence', [])
        if isinstance(sequence, list):
            print(json.dumps(sequence))
        else:
            print(json.dumps([]))
    except Exception:
        print(json.dumps([]))

if __name__ == "__main__":
    main()
