import json
import collections
import xml.etree.ElementTree as ET
import math

def find_path_with_obstacle_reporting(map_xml_string, start_x, start_y, end_x, end_y, is_surfing_str='false'):
    is_surfing = is_surfing_str.lower() == 'true' if is_surfing_str is not None else False
    root = ET.fromstring(map_xml_string)
    map_width = int(root.get('width'))
    map_height = int(root.get('height'))
    tiles = {}
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile_elem in row.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            has_object = tile_elem.get('has-object') == 'true'
            object_id, object_name = None, None
            if has_object:
                obj = tile_elem.find('Object')
                if obj is not None:
                    object_id = obj.get('id')
                    object_name = obj.get('id-name')
            tiles[(x, y)] = {'type': tile_type, 'has_object': has_object, 'object_id': object_id, 'object_name': object_name}

    start = (int(start_x), int(start_y))
    end_goal = (int(end_x), int(end_y))

    traversable_base_types = {'ground', 'grass', 'steps', 'elevated_ground', 'cleared_boulder_barrier', 'ladder_up', 'ladder_down'}
    if is_surfing:
        traversable_base_types.add('water')

    def get_traversability_reason(x, y):
        if not (1 <= x <= map_width and 1 <= y <= map_height): return 'out_of_bounds'
        tile_info = tiles.get((x, y))
        if not tile_info: return 'no_tile_info'
        if tile_info['type'] == 'unknown': return 'unseen_tile'
        if tile_info['type'] in ['impassable', 'boulder_barrier', 'closed_gate', 'ledge']: return f"{tile_info['type']}_tile"
        if tile_info['type'] == 'cuttable': return 'cuttable_tree'
        if not is_surfing and tile_info['type'] == 'water': return 'water_requires_surf'
        if tile_info['has_object'] and tile_info['object_id'] != '15': return f"object_{tile_info.get('object_name', 'Unknown')}"
        if tile_info['type'] not in traversable_base_types: return f"non_traversable_type_{tile_info['type']}"
        return 'traversable'

    def is_traversable(x, y):
        return get_traversability_reason(x, y) == 'traversable'

    goal_set = set()
    if is_traversable(end_goal[0], end_goal[1]):
        goal_set.add(end_goal)
    else:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (end_goal[0] + dx, end_goal[1] + dy)
            if is_traversable(neighbor[0], neighbor[1]):
                goal_set.add(neighbor)

    if not goal_set:
        reason = get_traversability_reason(end_goal[0], end_goal[1])
        obstacle_info = {'x': end_goal[0], 'y': end_goal[1], 'type': tiles.get(end_goal, {}).get('type', 'unknown'), 'reason': reason}
        print(json.dumps({'path_found': False, 'error': f'Destination and all adjacent tiles are not traversable. Blockage reason: {reason}', 'obstacle': obstacle_info}))
        return

    queue = collections.deque([(start, [start])])
    visited = {start}

    while queue:
        (current_x, current_y), path = queue.popleft()
        current_pos = (current_x, current_y)

        if current_pos in goal_set:
            print(json.dumps({'path_found': True, 'path': path}))
            return

        current_tile_info = tiles.get(current_pos)
        current_type = current_tile_info['type']

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor_pos = (current_x + dx, current_y + dy)
            if neighbor_pos in visited or not is_traversable(neighbor_pos[0], neighbor_pos[1]):
                continue

            neighbor_tile_info = tiles.get(neighbor_pos)
            neighbor_type = neighbor_tile_info['type']

            if current_type == 'ground' and neighbor_type == 'elevated_ground': continue
            if current_type == 'elevated_ground' and neighbor_type == 'ground': continue

            visited.add(neighbor_pos)
            new_path = list(path)
            new_path.append(neighbor_pos)
            queue.append((neighbor_pos, new_path))

    frontier_obstacles = []
    for (vx, vy) in visited:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (vx + dx, vy + dy)
            if 1 <= neighbor[0] <= map_width and 1 <= neighbor[1] <= map_height and neighbor not in visited:
                reason = get_traversability_reason(neighbor[0], neighbor[1])
                if reason != 'traversable':
                    tile_info = tiles.get(neighbor)
                    obstacle = {'x': neighbor[0], 'y': neighbor[1], 'type': tile_info.get('type'), 'reason': reason}
                    if 'object' in reason:
                        obstacle['object_name'] = tile_info.get('object_name', 'Unknown Object')
                    frontier_obstacles.append(obstacle)

    if not frontier_obstacles:
        if is_surfing and all(tiles.get(pos, {}).get('type') == 'water' for pos in visited):
            print(json.dumps({'path_found': False, 'error': 'No path found. The destination may be in an isolated body of water.', 'obstacle': None}))
            return
        print(json.dumps({'path_found': False, 'error': 'No path found. Could not identify a specific frontier obstacle.', 'obstacle': None}))
        return

    unique_obstacle_reasons = sorted(list(set(obs['reason'] for obs in frontier_obstacles)))
    reasons_str = ', '.join(unique_obstacle_reasons)
    error_message = 'Path blocked. Frontier contains obstacles: {}.'.format(reasons_str)
    print(json.dumps({'path_found': False, 'error': error_message, 'obstacles': frontier_obstacles}))

