from collections import deque
import json

try:
    # map_xml_string is injected by the environment
    root = ET.fromstring(map_xml_string)

    # 1. Find Player Position
    player_pos = None
    # Iterate through all tiles to find the one containing the Player element
    for row in root.findall("Row"):
        y = int(row.get("id"))
        for tile in row.findall("Tile"):
            if tile.find("Player") is not None:
                x = int(tile.get("id"))
                player_pos = (x, y)
                break
        if player_pos: break

    if not player_pos:
        print(json.dumps([])) # No player found
        exit()

    start = player_pos
