    if start == goal:
        print(json.dumps([]))
        exit()

    # 2. Build Grid / Identify Obstacles
    width = int(root.get("width"))
    height = int(root.get("height"))

    obstacles = set()

    # Define non-walkable types
    # Note: "DOOR", "FLOOR", "GRASS", "WARP_CARPET_..." are walkable
    # We must match the XML types exactly.
    COLLISION_TYPES = {
        "WALL", "WATER", "HEADBUTT_TREE", "CUT_TREE", "TREE", "ROCK", "BOULDER", "COUNTER", "PC", "SIGN", "WINDOW"
    }

    for row in root.findall("Row"):
        y = int(row.get("id"))
        for tile in row.findall("Tile"):
            x = int(tile.get("id"))
            t_type = tile.get("type")

            is_blocked = False

            # Check type collision
            if t_type in COLLISION_TYPES:
                is_blocked = True

            # Check Object collision (NPCs, Signs, Items block movement)
            # Exception: If the tile is a warp (like a door), it might have an object but be walkable?
            # Usually signs are on walls. NPCs are on floors.
            # We assume we cannot walk THROUGH an NPC.
            if tile.get("has-object") == "true":
                is_blocked = True

            # Special case: Ledges (Treat as blocked for simplicity in V1 to avoid one-way traps)
            if "LEDGE" in t_type:
                is_blocked = True

            if is_blocked:
                obstacles.add((x, y))

    # 3. BFS Pathfinding
    queue = deque([(start, [])])
    visited = set([start])

    path_buttons = []
    found = False

    while queue:
        (curr_x, curr_y), path = queue.popleft()

        if (curr_x, curr_y) == goal:
            # Reconstruct buttons
            curr = start
            for next_step in path:
                dx = next_step[0] - curr[0]
                dy = next_step[1] - curr[1]
                if dx == 1: path_buttons.append("Right")
                elif dx == -1: path_buttons.append("Left")
                elif dy == 1: path_buttons.append("Down")
                elif dy == -1: path_buttons.append("Up")
                curr = next_step
            found = True
            break

        # Explore neighbors (Up, Down, Left, Right)
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = curr_x + dx, curr_y + dy

            if 0 <= nx < width and 0 <= ny < height:
                if (nx, ny) not in visited and (nx, ny) not in obstacles:
                    visited.add((nx, ny))
                    queue.append(((nx, ny), path + [(nx, ny)]))

    if found:
        print(json.dumps(path_buttons))
    else:
        print(json.dumps([])) # No path found

except Exception as e:
    # Fail gracefully
    print(json.dumps([]))
