import xml.etree.ElementTree as ET
from collections import deque
import json

def run(input_data):
    root = ET.fromstring(map_xml_string)

    start_x = input_data.get('start_x')
    start_y = input_data.get('start_y')
    autopress = input_data.get('autopress_buttons', False)
    can_surf = input_data.get('can_surf', False)

    if start_x is None or start_y is None:
        found = False
        for row in root.findall('Row'):
            y = int(row.attrib['id'])
            for tile in row.findall('Tile'):
                if tile.find('Player') is not None:
                    start_x = int(tile.attrib['id'])
                    start_y = y
                    found = True
                    break
            if found: break

    if start_x is None or start_y is None:
        return {"error": "Player position not found"}

    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])

    grid = {}
    impassable_types = {'WALL', 'HEADBUTT_TREE', 'unseen'}
    if not can_surf:
        impassable_types.add('WATER')

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib['type']
            grid[(x, y)] = t_type
            if tile.find('Object') is not None:
                grid[(x, y)] = 'WALL'

    queue = deque([(int(start_x), int(start_y))])
    visited = {(int(start_x), int(start_y)): None}

    path = []
    while queue:
        curr_x, curr_y = queue.popleft()
        if (curr_x, curr_y) == (end_x, end_y):
            temp_x, temp_y = curr_x, curr_y
            while temp_x is not None and temp_y is not None:
                path.append({"x": temp_x, "y": temp_y})
                parent = visited[(temp_x, temp_y)]
                if parent:
                    temp_x, temp_y = parent
                else:
                    temp_x, temp_y = None, None
            path = path[::-1]
            break

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = curr_x + dx, curr_y + dy
            if (nx, ny) in grid:
                t_type = grid[(nx, ny)]
                if t_type in impassable_types and (nx, ny) != (end_x, end_y):
                    continue

                if dy == 1: # South
                    if grid.get((curr_x, curr_y)) == 'FLOOR_UP_WALL' or t_type == 'FLOOR_UP_WALL': continue
                    if t_type == 'LEDGE_HOP_DOWN':
                        ny = ny + 1
                        if (nx, ny) not in grid or grid[(nx, ny)] in impassable_types: continue
                elif dy == -1: # North
                    if t_type == 'LEDGE_HOP_DOWN': continue
                elif dx == 1: # East
                    if t_type == 'LEDGE_HOP_RIGHT':
                        nx = nx + 1
                        if (nx, ny) not in grid or grid[(nx, ny)] in impassable_types: continue
                elif dx == -1: # West
                    if t_type == 'LEDGE_HOP_RIGHT': continue

                if (nx, ny) not in visited:
                    visited[(nx, ny)] = (curr_x, curr_y)
                    queue.append((nx, ny))

    if not path:
        return []

    if autopress:
        buttons = []
        for i in range(len(path) - 1):
            curr = path[i]
            nxt = path[i+1]
            if nxt['x'] > curr['x']: buttons.append('Right')
            elif nxt['x'] < curr['x']: buttons.append('Left')
            elif nxt['y'] > curr['y']: buttons.append('Down')
            elif nxt['y'] < curr['y']: buttons.append('Up')
        return buttons
    else:
        return path

print(json.dumps(run(input_data)))

