import collections
import xml.etree.ElementTree as ET

def solve():
    root = ET.fromstring(map_xml_string)
    grid = {}
    player_pos = None
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            t_type = tile.get('type')
            seen = tile.get('seen') == 'true'
            has_obj = tile.find('Object') is not None
            grid[(x, y)] = (t_type, seen, has_obj)
            if tile.get('has-player') == 'true':
                player_pos = (x, y)

    if player_pos is None:
        print("Player position not found in map.")
        return

    def is_passable(pos):
        if pos not in grid: return False
        t_type, seen, has_obj = grid[pos]
        if not seen: return False
        # Simplified list of walkable tiles for exploration
        walkable_types = ['FLOOR', 'TALL_GRASS', 'LONG_GRASS', 'WARP_CARPET_LEFT', 'WARP_CARPET_RIGHT', 'WARP_CARPET_UP', 'WARP_CARPET_DOWN', 'WARP', 'CAVE', 'DOOR']
        return t_type in walkable_types and not has_obj

    queue = collections.deque([player_pos])
    visited = {player_pos}
    reachable_unseen = set()

    while queue: 
        cx, cy = queue.popleft()
        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            nx, ny = cx + dx, cy + dy
            npos = (nx, ny)
            if npos in grid:
                _, seen, _ = grid[npos]
                if not seen:
                    reachable_unseen.add(npos)
                elif is_passable(npos) and npos not in visited:
                    visited.add(npos)
                    queue.append(npos)

    print(f'Reachable Unseen Tiles: {sorted(list(reachable_unseen))}')

solve()
