import xml.etree.ElementTree as ET
import json

def is_traversable(x, y, grid, width, height, ignore_boulders=False):
    if not (1 <= x <= width and 1 <= y <= height):
        return False
    tile = grid[y - 1][x - 1]
    # Boulders cannot be pushed onto switches that already have boulders
    if tile['type'] == 'boulder_switch' and tile['has_boulder']:
        return False
    if tile['type'] in ['impassable', 'unknown', 'boulder_barrier']:
        return False
    if tile['has_impassable_object']:
        return False
    if not ignore_boulders and tile['has_boulder']:
        return False
    return True

try:
    tree = ET.fromstring(map_xml_string)
    width = int(tree.get('width'))
    height = int(tree.get('height'))

    grid = []
    boulders = []
    # First pass: build grid and identify boulder locations and other objects
    for y_idx, row_elem in enumerate(tree.findall('Row')):
        row = []
        for x_idx, tile_elem in enumerate(row_elem.findall('Tile')):
            tile_data = {
                'type': tile_elem.get('type'),
                'has_impassable_object': False,
                'has_boulder': False
            }
            obj_elem = tile_elem.find('Object')
            if obj_elem is not None and obj_elem.get('id-name', '') not in ['Pikachu', 'Unknown', 'PokÃ© Ball']:
                 tile_data['has_impassable_object'] = True

            if tile_elem.find('Boulder') is not None:
                tile_data['has_boulder'] = True
                boulders.append({'pos': {'x': x_idx + 1, 'y': y_idx + 1}})

            row.append(tile_data)
        grid.append(row)

    possible_moves = []
    for i, boulder in enumerate(boulders):
        boulder_moves = {'boulder_id': i + 1, 'boulder_pos': boulder['pos'], 'moves': []}
        bx, by = boulder['pos']['x'], boulder['pos']['y']

        directions = [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]

        for dx, dy, direction in directions:
            dest_x, dest_y = bx + dx, by + dy
            player_adj_x, player_adj_y = bx - dx, by - dy
            player_remote_x, player_remote_y = bx - (2 * dx), by - (2 * dy)

            if is_traversable(dest_x, dest_y, grid, width, height):
                if is_traversable(player_adj_x, player_adj_y, grid, width, height, ignore_boulders=True):
                    boulder_moves['moves'].append({
                        'direction': direction,
                        'type': 'adjacent',
                        'player_pos': {'x': player_adj_x, 'y': player_adj_y}
                    })

                # Remote push is only possible if the space between player and boulder is clear
                if is_traversable(player_adj_x, player_adj_y, grid, width, height, ignore_boulders=True) and is_traversable(player_remote_x, player_remote_y, grid, width, height, ignore_boulders=True):
                    boulder_moves['moves'].append({
                        'direction': direction,
                        'type': 'remote',
                        'player_pos': {'x': player_remote_x, 'y': player_remote_y}
                    })

        possible_moves.append(boulder_moves)

    print(json.dumps(possible_moves))

except Exception as e:
    print(json.dumps({'error': str(e), 'line': e.__traceback__.tb_lineno}))

