        for x in range(1, width + 1):
            if (x, y) not in visited:
                tile = grid[y - 1][x - 1]
                is_impassable = tile['type'] in {'impassable', 'unknown'} or tile['has_impassable_object'] or tile['has_boulder']
                if tile['type'] == 'boulder_barrier' and not assume_barriers_open:
                    is_impassable = True

                if not is_impassable:
                    current_landmass = {'id': landmass_id_counter, 'type': 'land', 'coordinates': []}
                    q = deque([(x, y)])
                    visited.add((x, y))

                    while q:
                        cx, cy = q.popleft()
                        current_landmass['coordinates'].append((cx, cy))
                        for nx, ny in get_neighbors(cx, cy, grid, width, height, assume_barriers_open):
                            if (nx, ny) not in visited:
                                visited.add((nx, ny))
                                q.append((nx, ny))

                    landmasses.append(current_landmass)
                    landmass_id_counter += 1

    result = {'landmasses': landmasses}
    if start_x and start_y:
        for lm in landmasses:
            if (start_x, start_y) in lm['coordinates']:
                result['start_point_landmass'] = lm['id']
                break
    if end_x and end_y:
        for lm in landmasses:
            if (end_x, end_y) in lm['coordinates']:
                result['end_point_landmass'] = lm['id']
                break

    print(json.dumps(result))

except Exception as e:
    print(json.dumps({'error': str(e), 'line': e.__traceback__.tb_lineno}))
