import xml.etree.ElementTree as ET
import heapq

def parse_map(xml_string):
    root = ET.fromstring(xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])
    grid = [[None for _ in range(height)] for _ in range(width)]
    start = None

    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            type_ = tile.attrib['type']
            grid[x][y] = type_

            if tile.get('has-player') == 'true':
                start = (x, y)
            if start is None:
                for child in tile:
                    if child.tag == 'Player':
                        start = (x, y)

    return grid, width, height, start

def is_walkable(tile_type):
    return tile_type in ["FLOOR", "GRASS", "WATER", "SAND", "WARP_CARPET_DOWN", "WARP_CARPET_UP", "WARP_CARPET_LEFT", "WARP_CARPET_RIGHT"]

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def a_star(grid, start, goal, width, height):
    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = {start: 0}
    f_score = {start: heuristic(start, goal)}

    while open_set:
        current = heapq.heappop(open_set)[1]

        if current == goal:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start)
            return path[::-1]

        neighbors = [
            (current[0] + 1, current[1]),
            (current[0] - 1, current[1]),
            (current[0], current[1] + 1),
            (current[0], current[1] - 1)
        ]

        for neighbor in neighbors:
            nx, ny = neighbor
            if 0 <= nx < width and 0 <= ny < height:
                tile_type = grid[nx][ny]

                if tile_type is None and neighbor != goal: 
                     continue 
                if tile_type is not None and not is_walkable(tile_type) and neighbor != goal:
                    continue

                tentative_g_score = g_score[current] + 1
                if neighbor not in g_score or tentative_g_score < g_score[neighbor]:
                    came_from[neighbor] = current
                    g_score[neighbor] = tentative_g_score
                    f_score[neighbor] = tentative_g_score + heuristic(neighbor, goal)
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))
    return None

