import xml.etree.ElementTree as ET
import heapq
import json

def get_tile(x, y, grid, width, height):
    if not (1 <= x <= width and 1 <= y <= height):
        return None
    return grid[y - 1][x - 1]

def is_boulder_traversable(x, y, grid, width, height, occupied_tiles):
    tile = get_tile(x, y, grid, width, height)
    if not tile or tile['type'] in ['impassable', 'unknown', 'boulder_barrier', 'steps'] or (x, y) in occupied_tiles or tile.get('has_impassable_object'):
        return False
    return True

def is_player_traversable(x, y, grid, width, height, occupied_tiles):
    tile = get_tile(x, y, grid, width, height)
    if not tile or tile['type'] in ['impassable', 'unknown', 'boulder_barrier', 'steps', 'hole'] or (x, y) in occupied_tiles or tile.get('has_impassable_object'):
        return False
    return True

def get_player_path(start, end, grid, width, height, occupied_tiles):
    open_set = [(0, start)]
    came_from = {}
    g_score = {start: 0}

    while open_set:
        _, current = heapq.heappop(open_set)
        if current == end:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            return path[::-1]

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            if is_player_traversable(neighbor[0], neighbor[1], grid, width, height, occupied_tiles):
                tentative_g_score = g_score[current] + 1
                if tentative_g_score < g_score.get(neighbor, float('inf')):
                    came_from[neighbor] = current
                    g_score[neighbor] = tentative_g_score
                    f_score = tentative_g_score + abs(neighbor[0] - end[0]) + abs(neighbor[1] - end[1])
                    heapq.heappush(open_set, (f_score, neighbor))
    return None

try:
    boulder_start_x = int(input_data['boulder_start_x'])
    boulder_start_y = int(input_data['boulder_start_y'])
    boulder_end_x = int(input_data['boulder_end_x'])
    boulder_end_y = int(input_data['boulder_end_y'])
    player_start_x = int(input_data['player_start_x'])
    player_start_y = int(input_data['player_start_y'])

    tree = ET.fromstring(map_xml_string)
    width = int(tree.get('width'))
    height = int(tree.get('height'))

    grid = []
    all_boulders = set()
    for y, row_elem in enumerate(tree.findall('Row')):
        row = []
        for x, tile_elem in enumerate(row_elem.findall('Tile')):
            tile_data = {'type': tile_elem.get('type'), 'has_impassable_object': False}
            if tile_elem.find('Object') is not None and tile_elem.find('Object').get('id-name', '') not in ['Pikachu', 'Unknown', 'Poké Ball']:
                tile_data['has_impassable_object'] = True
            if tile_elem.find('Boulder') is not None:
                all_boulders.add((x + 1, y + 1))
            row.append(tile_data)
        grid.append(row)

    start_state = ((player_start_x, player_start_y), (boulder_start_x, boulder_start_y))
    goal_boulder_pos = (boulder_end_x, boulder_end_y)

    open_set = [(0, start_state)]
    came_from = {start_state: None}
    g_score = {start_state: 0}

    solution_found = False
    final_state = None

    other_boulders = all_boulders - {(boulder_start_x, boulder_start_y)}

    while open_set:
        _, current_state = heapq.heappop(open_set)
        player_pos, boulder_pos = current_state

        if boulder_pos == goal_boulder_pos:
            solution_found = True
            final_state = current_state
            break

        for dx, dy, direction in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
            player_push_pos = (boulder_pos[0] - dx, boulder_pos[1] - dy)
            boulder_dest_pos = (boulder_pos[0] + dx, boulder_pos[1] + dy)

            if is_boulder_traversable(boulder_dest_pos[0], boulder_dest_pos[1], grid, width, height, other_boulders):
                current_obstacles = other_boulders | {boulder_pos}
                path_to_push = get_player_path(player_pos, player_push_pos, grid, width, height, current_obstacles)

                if path_to_push is not None:
                    new_player_pos = player_push_pos
                    new_boulder_pos = boulder_dest_pos
                    new_state = (new_player_pos, new_boulder_pos)

                    cost = len(path_to_push) + 1
                    tentative_g_score = g_score[current_state] + cost

                    if tentative_g_score < g_score.get(new_state, float('inf')):
                        action_plan = {'move_path': path_to_push, 'push_direction': direction}
                        came_from[new_state] = (current_state, action_plan)
                        g_score[new_state] = tentative_g_score
                        f_score = tentative_g_score + abs(new_boulder_pos[0] - goal_boulder_pos[0]) + abs(new_boulder_pos[1] - goal_boulder_pos[1])
                        heapq.heappush(open_set, (f_score, new_state))

    if solution_found:
        plan = []
        state = final_state
        while state in came_from and came_from[state] is not None:
            prev_state, action = came_from[state]
            plan.append(action)
            state = prev_state
        print(json.dumps(plan[::-1]))
    else:
        print(json.dumps({'error': 'No solution found'}))

except Exception as e:
    import traceback
    print(json.dumps({'error': str(e), 'traceback': traceback.format_exc()}))

