import xml.etree.ElementTree as ET
import heapq
import json

def get_tile(x, y, grid):
    if 1 <= y <= len(grid) and 1 <= x <= len(grid[0]):
        return grid[y-1][x-1]
    return None

def get_neighbors(x, y, grid, width, height, assume_barriers_open, movement_mode):
    neighbors = []
    current_tile_data = get_tile(x, y, grid)
    if not current_tile_data: return []
    current_type = current_tile_data['type']

    if movement_mode == 'surfing':
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if not (1 <= nx <= width and 1 <= ny <= height): continue
            neighbor_tile = get_tile(nx, ny, grid)
            if neighbor_tile and neighbor_tile['type'] == 'water' and not neighbor_tile.get('has_impassable_object'):
                neighbors.append((nx, ny))
        return neighbors

    ground_likes = ['ground', 'grass', 'boulder_switch', 'ladder_up', 'ladder_down']

    for dx, dy, direction in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
        nx, ny = x + dx, y + dy
        if not (1 <= nx <= width and 1 <= ny <= height): continue

        neighbor_tile_data = get_tile(nx, ny, grid)
        if not neighbor_tile_data: continue
        neighbor_type = neighbor_tile_data['type']

        if assume_barriers_open and neighbor_type == 'boulder_barrier':
            neighbor_type = 'cleared_boulder_barrier'

        if neighbor_tile_data.get('has_impassable_object') or neighbor_tile_data.get('has_boulder'): continue
        if neighbor_type in ['impassable', 'unknown', 'water', 'boulder_barrier', 'hole']: continue

        final_pos = (nx, ny)

        if current_type in ground_likes:
            if neighbor_type in ground_likes or neighbor_type == 'steps':
                neighbors.append(final_pos)
            elif neighbor_type == 'cleared_boulder_barrier' and direction == 'Up':
                neighbors.append(final_pos)
            elif neighbor_type == 'ledge' and direction == 'Down':
                final_pos = (nx, ny + 1)
                if 1 <= final_pos[1] <= height:
                    landing_tile = get_tile(final_pos[0], final_pos[1], grid)
                    if landing_tile and landing_tile['type'] not in ['impassable', 'unknown', 'hole']:
                        neighbors.append(final_pos)

        elif current_type == 'steps':
            if neighbor_type in ground_likes or neighbor_type == 'elevated_ground' or neighbor_type == 'steps':
                 neighbors.append(final_pos)

        elif current_type == 'elevated_ground':
            if neighbor_type in ['elevated_ground', 'steps', 'cleared_boulder_barrier']:
                 neighbors.append(final_pos)
            elif neighbor_type in ground_likes and direction == 'Down':
                 neighbors.append(final_pos)
            elif neighbor_type == 'ledge' and direction == 'Down':
                final_pos = (nx, ny + 1)
                if 1 <= final_pos[1] <= height:
                    landing_tile = get_tile(final_pos[0], final_pos[1], grid)
                    if landing_tile and landing_tile['type'] not in ['impassable', 'unknown', 'hole']:
                        neighbors.append(final_pos)

        elif current_type == 'cleared_boulder_barrier':
            if neighbor_type in ['cleared_boulder_barrier', 'elevated_ground']:
                neighbors.append(final_pos)
            elif neighbor_type in ground_likes and direction == 'Down':
                neighbors.append(final_pos)

    return list(set(neighbors))

def a_star_search(start, end, grid, width, height, assume_barriers_open, movement_mode):
    open_set = []
    heapq.heappush(open_set, (0, start))
    came_from = {}
    g_score = {start: 0}
    f_score = {start: abs(start[0] - end[0]) + abs(start[1] - end[1])}

    while open_set:
        _, current = heapq.heappop(open_set)
        if current == end:
            path = []
            while current in came_from:
                path.append({'x': current[0], 'y': current[1]})
                current = came_from[current]
            path.append({'x': start[0], 'y': start[1]})
            return path[::-1]

        for neighbor in get_neighbors(current[0], current[1], grid, width, height, assume_barriers_open, movement_mode):
            cost = abs(neighbor[0] - current[0]) + abs(neighbor[1] - current[1])
            tentative_g_score = g_score[current] + cost
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + abs(neighbor[0] - end[0]) + abs(neighbor[1] - end[1])
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))
    return None

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    movement_mode = input_data.get('movement_mode', 'walking')
    assume_barriers_open = str(input_data.get('assume_barriers_open', 'false')).lower() == 'true'
    ignore_object_ids_str = input_data.get('ignore_object_ids')
    ignore_object_ids = [int(i.strip()) for i in ignore_object_ids_str.split(',')] if ignore_object_ids_str else []

    tree = ET.fromstring(map_xml_string)
    width = int(tree.get('width'))
    height = int(tree.get('height'))

    grid = []
    for y, row_elem in enumerate(tree.findall('Row')):
        row = []
        for x, tile_elem in enumerate(row_elem.findall('Tile')):
            tile_data = {
                'type': tile_elem.get('type'),
                'has_impassable_object': False,
                'has_boulder': False
            }
            obj_elem = tile_elem.find('Object')
            if obj_elem is not None and obj_elem.get('id-name', '') not in ['Pikachu', 'Unknown', 'Poké Ball']:
                obj_id_str = obj_elem.get('id')
                if obj_id_str and obj_id_str.isdigit() and int(obj_id_str) not in ignore_object_ids:
                    tile_data['has_impassable_object'] = True
            if tile_elem.find('Boulder') is not None:
                tile_data['has_boulder'] = True
            row.append(tile_data)
        grid.append(row)

    path = a_star_search((start_x, start_y), (end_x, end_y), grid, width, height, assume_barriers_open, movement_mode)

    if not path:
        potential_ends = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = end_x + dx, end_y + dy
            tile = get_tile(nx, ny, grid)
            if tile and tile['type'] not in {'impassable', 'unknown', 'hole'} and not tile.get('has_impassable_object'):
                potential_ends.append((nx,ny))

        if potential_ends:
            best_path = None
            min_len = float('inf')
            for p_end in potential_ends:
                temp_path = a_star_search((start_x, start_y), p_end, grid, width, height, assume_barriers_open, movement_mode)
                if temp_path and len(temp_path) < min_len:
                    min_len = len(temp_path)
                    best_path = temp_path
            path = best_path

    if path:
        print(json.dumps(path))
    else:
        print(json.dumps({"error": "No path found"}))

except Exception as e:
    import traceback
    print(json.dumps({"error": str(e), "traceback": traceback.format_exc()}))
