import xml.etree.ElementTree as ET
import heapq
import json

def pathfinder_tool(map_xml_string, input_data):
    print(f"RAW_INPUT_RECEIVED: type={type(input_data)}, value={repr(input_data)}")
    try:
        target_x = int(input_data['target_x'])
        target_y = int(input_data['target_y'])
        party_data = json.loads(input_data['party_data'])
        impassable_coords_str = input_data.get('impassable_coords')
        impassable_coords_set = set(json.loads(impassable_coords_str)) if impassable_coords_str and impassable_coords_str != 'null' else set()

        has_surf = any('SURF' in move['name'] for pokemon in party_data for move in pokemon.get('moves', []))

        root = ET.fromstring(map_xml_string)
        map_width = int(root.get('width'))
        map_height = int(root.get('height'))

        tile_map = {}
        player_pos = None

        for row_elem in root.findall('Row'):
            for tile_elem in row_elem.findall('Tile'):
                coord_str = tile_elem.get('coordinate')
                x_coord, y_coord = map(int, coord_str.strip('()').split(','))
                tile_data = {
                    'type': tile_elem.get('type'),
                    'has_object': tile_elem.get('has-object') == 'true',
                    'object_id_name': None
                }
                obj_elem = tile_elem.find('Object')
                if obj_elem is not None:
                    tile_data['object_id_name'] = obj_elem.get('id-name')
                if tile_elem.find('Player') is not None:
                    player_pos = (x_coord, y_coord)
                tile_map[(x_coord, y_coord)] = tile_data

        if not player_pos:
            return json.dumps({'error': 'Player position not found.'})

        def is_traversable(coord, from_coord, grid):
            tile = grid.get(coord)
            if not tile: return False
            if f'({coord[0]}, {coord[1]})' in impassable_coords_set: return False

            if tile['has_object'] and tile.get('object_id_name') != 'Pikachu':
                return False

            tile_type = tile['type']
            from_tile_type = grid.get(from_coord)['type'] if from_coord else 'none'

            if tile_type == 'ledge': return from_coord[1] == coord[1] - 1
            if from_tile_type == 'ledge': return coord[0] == from_coord[0] and coord[1] == from_coord[1] + 1

            walkable_types = ['ground', 'grass', 'steps', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen']

            def check_type_walkable(t_type):
                if t_type in walkable_types: return True
                if t_type == 'water' and has_surf: return True
                return False

            is_tile_walkable = check_type_walkable(tile_type)
            if not is_tile_walkable: return False

            if from_coord and tile_type != from_tile_type:
                is_from_walkable = check_type_walkable(from_tile_type)
                if is_from_walkable and is_tile_walkable: 
                    return True

            return True

        start_node = player_pos
        end_node = (target_x, target_y)

        if not is_traversable(end_node, None, tile_map):
            found_adjacent = False
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                adjacent_node = (end_node[0] + dx, end_node[1] + dy)
                if is_traversable(adjacent_node, None, tile_map):
                    end_node = adjacent_node
                    found_adjacent = True
                    break
            if not found_adjacent:
                return json.dumps({'error': 'No reachable adjacent tile found for the impassable target.'})

        open_set = [(0, start_node)]
        came_from = {}
        g_score = {start_node: 0}
        f_score = {start_node: abs(start_node[0] - end_node[0]) + abs(start_node[1] - end_node[1])}

        path_iterations = 0
        while open_set:
            path_iterations += 1
            if path_iterations > 5000: # Safety break
                return json.dumps({'error': 'Pathfinding exceeded iteration limit.'})

            _, current = heapq.heappop(open_set)

            if current == end_node:
                path = []
                while current in came_from:
                    path.append({'x': current[0], 'y': current[1]})
                    current = came_from[current]
                path.append({'x': start_node[0], 'y': start_node[1]})
                return json.dumps(path[::-1])

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                neighbor = (current[0] + dx, current[1] + dy)
                if not (1 <= neighbor[0] <= map_width and 1 <= neighbor[1] <= map_height): continue
                if not is_traversable(neighbor, current, tile_map): continue

                tentative_g_score = g_score[current] + 1
                if neighbor not in g_score or tentative_g_score < g_score.get(neighbor, float('inf')):
                    came_from[neighbor] = current
                    g_score[neighbor] = tentative_g_score
                    f_score[neighbor] = tentative_g_score + abs(neighbor[0] - end_node[0]) + abs(neighbor[1] - end_node[1])
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

        return json.dumps({'error': 'No path found.'})
    except Exception as e:
        print(f"DEBUG_CAUGHT_EXCEPTION: {repr(e)}")
        return json.dumps({'error': 'An exception occurred during pathfinding.'})

