import xml.etree.ElementTree as ET
import json
import sys

def find_closest_unseen_tile(map_xml_string, input_data):
    try:
        party_data = json.loads(input_data['party_data'])

        def parse_map(xml_string):
            root = ET.fromstring(xml_string)
            grid = {}
            player_pos = None
            unseen_tiles = []
            for row_elem in root.findall('Row'):
                y = int(row_elem.get('id'))
                for tile_elem in row_elem.findall('Tile'):
                    x = int(tile_elem.get('id'))
                    tile_type = tile_elem.get('type')
                    seen = tile_elem.get('seen') == 'true'
                    has_object = tile_elem.get('has-object') == 'true'
                    object_id_name = None
                    obj_elem = tile_elem.find('Object')
                    if obj_elem is not None:
                        object_id_name = obj_elem.get('id-name')
                    if tile_elem.find('Player') is not None:
                        player_pos = (x, y)
                    grid[(x, y)] = {'type': tile_type, 'has_object': has_object, 'object_id_name': object_id_name, 'seen': seen}
                    if not seen:
                        unseen_tiles.append((x, y))
            return grid, player_pos, unseen_tiles, int(root.get('width')), int(root.get('height'))

        has_surf = any('SURF' in move for pokemon in party_data for move in pokemon.get('moves', []))
        has_cut = any('CUT' in move for pokemon in party_data for move in pokemon.get('moves', []))

        def is_traversable_for_bfs(coord, from_coord, grid):
            tile = grid.get(coord)
            if not tile or not tile['seen']: return False
            if tile['has_object'] and tile['object_id_name'] != 'Pikachu': return False
            from_tile = grid.get(from_coord)
            if not from_tile: return False

            if tile['type'] == 'ledge': return from_coord[1] == coord[1] - 1
            if from_tile['type'] == 'ledge': return coord[0] == from_coord[0] and coord[1] == from_coord[1] + 1

            if tile['type'] == 'water':
                if from_tile['type'] in ['ground', 'grass', 'steps']: return has_surf
                return from_tile['type'] == 'water'

            if tile['type'] == 'cuttable':
                if not has_cut: return False
            elif tile['type'] in ['unknown', 'impassable']: return False

            walkable_land = ['ground', 'grass', 'steps', 'cleared_boulder_barrier', 'open_gate', 'gate_offscreen', 'cuttable']
            if tile['type'] in walkable_land and from_tile['type'] in walkable_land: return True
            if from_tile['type'] == 'water' and tile['type'] in walkable_land: return True

            return False

        def get_neighbors(node, grid, map_width, map_height):
            neighbors = []
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                neighbor = (node[0] + dx, node[1] + dy)
                if 1 <= neighbor[0] <= map_width and 1 <= neighbor[1] <= map_height:
                    if is_traversable_for_bfs(neighbor, node, grid):
                        neighbors.append(neighbor)
            return neighbors

        def bfs_find_reachable(start, grid, map_width, map_height):
            frontier = [start]
            reachable = {start}
            while frontier:
                current = frontier.pop(0)
                for neighbor in get_neighbors(current, grid, map_width, map_height):
                    if neighbor not in reachable:
                        reachable.add(neighbor)
                        frontier.append(neighbor)
            return reachable

        grid, player_pos, unseen_tiles, map_width, map_height = parse_map(map_xml_string)
        if player_pos is None: raise ValueError('Player position not found.')

        reachable_seen_tiles = bfs_find_reachable(player_pos, grid, map_width, map_height)

        border_unseen_tiles = []
        for tile_coord in unseen_tiles:
            x, y = tile_coord
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                neighbor = (x + dx, y + dy)
                if neighbor in reachable_seen_tiles:
                    border_unseen_tiles.append(tile_coord)
                    break

        if not border_unseen_tiles:
            print(json.dumps({'message': 'No reachable unseen tiles found.'}))
            return

        closest_tile = min(border_unseen_tiles, key=lambda t: abs(t[0] - player_pos[0]) + abs(t[1] - player_pos[1]))

        for dx, dy in sorted([(0, 1), (0, -1), (1, 0), (-1, 0)], key=lambda p: (abs((closest_tile[0] + p[0]) - player_pos[0]) + abs((closest_tile[1] + p[1]) - player_pos[1]))):
            adj_tile = (closest_tile[0] + dx, closest_tile[1] + dy)
            if adj_tile in reachable_seen_tiles:
                print(json.dumps({'closest_reachable_unseen_tile': {'x': closest_tile[0], 'y': closest_tile[1]}, 'adjacent_tile': {'x': adj_tile[0], 'y': adj_tile[1]}}))
                return

        print(json.dumps({'message': 'Found a reachable unseen tile, but no reachable adjacent tile to path to.'}))

    except Exception as e:
        import traceback
        print(json.dumps({'error': str(e), 'traceback': traceback.format_exc()}))

find_closest_unseen_tile(map_xml_string, input_data)
