import xml.etree.ElementTree as ET
import json
from collections import deque

def find_path():
    try:
        root = ET.fromstring(map_xml_string)
    except ET.ParseError:
        print(json.dumps({'error': 'Failed to parse map XML.'}))
        return

    map_width = int(root.get('width'))
    map_height = int(root.get('height'))
    target_x = int(input_data['target_x'])
    target_y = int(input_data['target_y'])

    player_pos = None
    player_tile_type = None
    grid = {}
    on_screen_objects = set()

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            grid[(x, y)] = tile_elem
            if tile_elem.find('Player') is not None:
                player_pos = (x, y)
                player_tile_type = tile_elem.get('type')
            if tile_elem.get('has-object') == 'true':
                on_screen_objects.add((x, y))

    if not player_pos:
        print(json.dumps({'error': 'Player position not found.'}))
        return

    off_screen_obstacles = set()
    for row_elem in root.findall('Row'):
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            y = int(row_elem.get('id'))
            for marker in tile_elem.findall('Marker'):
                if marker.get('emoji') == 'üìç' and (x, y) not in on_screen_objects:
                    off_screen_obstacles.add((x, y))

    all_obstacles = on_screen_objects.union(off_screen_obstacles)

    impassable_tile_types = {
        'WALL', 'VOID', 'PC', 'TV', 'COUNTER', 'BOOKSHELF',
        'TOWN_MAP', 'POKEDEX', 'RADIO', 'PILLAR', 'MART_SHELF',
        'INCENSE_BURNER', 'CUT_TREE', 'HEADBUTT_TREE', 'BUOY', 'unseen', 'WHIRLPOOL'
    }

    if player_tile_type != 'WATER':
        impassable_tile_types.add('WATER')

    queue = deque([(player_pos, [])])
    visited = {player_pos}

    target_is_impassable = False
    target_tile = grid.get((target_x, target_y))
    if target_tile is not None:
        if (target_tile.get('type') in impassable_tile_types or (target_x, target_y) in all_obstacles):
            target_is_impassable = True
    else: 
        target_is_impassable = True

    while queue:
        (x, y), path = queue.popleft()

        if not target_is_impassable and (x, y) == (target_x, target_y):
            print(json.dumps(path + [{'x': x, 'y': y}]))
            return

        if target_is_impassable:
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                if (x + dx, y + dy) == (target_x, target_y):
                    print(json.dumps(path + [{'x': x, 'y': y}]))
                    return

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            next_x, next_y = x + dx, y + dy

            if not (0 <= next_x < map_width and 0 <= next_y < map_height):
                continue
            if (next_x, next_y) in visited:
                continue

            tile = grid.get((next_x, next_y))
            if tile is None: continue

            tile_type = tile.get('type')
            is_obstacle = (next_x, next_y) in all_obstacles

            if tile_type in impassable_tile_types or is_obstacle:
                continue

            if tile.find('Warp') is not None and (next_x, next_y) != (target_x, target_y):
                continue

            current_tile = grid.get((x, y))
            if current_tile is None: continue
            current_tile_type = current_tile.get('type')

            # Ledge logic - Rules for moving ONTO a ledge tile
            if tile_type == 'LEDGE_HOP_DOWN' and dy == -1: continue
            if tile_type == 'LEDGE_HOP_LEFT' and dx == 1: continue
            if tile_type == 'LEDGE_HOP_RIGHT' and dx == -1: continue

            # Ledge logic - Rules for moving FROM a ledge tile
            if current_tile_type == 'LEDGE_HOP_DOWN' and dy == -1: continue
            if current_tile_type == 'LEDGE_HOP_LEFT' and dx == 1: continue
            if current_tile_type == 'LEDGE_HOP_RIGHT' and dx == -1: continue

            if tile_type == 'FLOOR_UP_WALL' and dy == 1:
                continue

            visited.add((next_x, next_y))
            new_path = path + [{'x': x, 'y': y}]
            queue.append(((next_x, next_y), new_path))

    print(json.dumps({'error': 'No path found.'}))

find_path()

