import json

menu_options_str = input_data.get('menu_options', '[]')
target_option = input_data.get('target_option')

try:
    menu_options = json.loads(menu_options_str)
except json.JSONDecodeError:
    print(json.dumps({'error': 'Invalid JSON in menu_options'}))
    exit()

if not isinstance(menu_options, list):
    print(json.dumps({'error': 'menu_options must be a list'}))
    exit()

if target_option not in menu_options:
    print(json.dumps({'error': f'Target option "{target_option}" not found in menu.'}))
    exit()

current_index = 0 # Assuming cursor starts at the top
target_index = menu_options.index(target_option)

presses = []
if target_index > current_index:
    for _ in range(target_index - current_index):
        presses.append('Down')
elif target_index < current_index:
    for _ in range(current_index - target_index):
        presses.append('Up')

presses.append('A')
print(json.dumps(presses))

