import xml.etree.ElementTree as ET
import json
from collections import deque

def plan_step():
    try:
        root = ET.fromstring(map_xml_string)
    except ET.ParseError:
        print(json.dumps({'error': 'Failed to parse map XML.'}))
        return

    map_width = int(root.get('width'))
    map_height = int(root.get('height'))

    player_pos = None
    player_tile_type = None
    grid = {}
    all_objects = set()

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            grid[(x, y)] = tile_elem
            if tile_elem.find('Player') is not None:
                player_pos = (x, y)
                player_tile_type = tile_elem.get('type')
            if tile_elem.get('has-object') == 'true':
                all_objects.add((x, y))

    if not player_pos:
        print(json.dumps({'error': 'Player position not found.'}))
        return

    impassable_tile_types = {
        'WALL', 'VOID', 'PC', 'TV', 'COUNTER', 'BOOKSHELF',
        'TOWN_MAP', 'POKEDEX', 'RADIO', 'PILLAR', 'MART_SHELF',
        'INCENSE_BURNER', 'CUT_TREE', 'HEADBUTT_TREE', 'BUOY', 'WHIRLPOOL'
    }
    if player_tile_type != 'WATER':
        impassable_tile_types.add('WATER')

    # BFS to find all reachable SEEN tiles
    bfs_q = deque([player_pos])
    reachable_seen_tiles = {player_pos}

    while bfs_q:
        (x, y) = bfs_q.popleft()

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            next_x, next_y = x + dx, y + dy

            if (next_x, next_y) in reachable_seen_tiles: continue

            tile = grid.get((next_x, next_y))
            if not tile: continue

            # Only expand on seen, traversable tiles
            if tile.get('seen') == 'true':
                if tile.get('type') in impassable_tile_types or (next_x, next_y) in all_objects or tile.find('Warp') is not None:
                    continue

                current_tile_type = grid.get((x,y)).get('type')
                if current_tile_type == 'LEDGE_HOP_DOWN' and dy != 1: continue
                if tile.get('type') == 'LEDGE_HOP_DOWN' and (dy == -1 or dx != 0): continue
                if tile.get('type') == 'FLOOR_UP_WALL' and dy == 1: continue

                reachable_seen_tiles.add((next_x, next_y))
                bfs_q.append((next_x, next_y))

    # Now, find all unseen tiles adjacent to the reachable seen tiles
    reachable_unseen_tiles = set()
    for (x, y) in reachable_seen_tiles:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_x, adj_y = x + dx, y + dy
            adj_tile = grid.get((adj_x, adj_y))
            if adj_tile and adj_tile.get('seen') == 'false':
                reachable_unseen_tiles.add((adj_x, adj_y))

    if not reachable_unseen_tiles:
        print(json.dumps({'target_x': None, 'target_y': None, 'reasoning': 'No unseen tiles are reachable from the current position.'}))
        return

    # Find the largest contiguous block of unseen tiles
    max_block_size = 0
    best_target = None
    checked_tiles = set()

    for start_x, start_y in reachable_unseen_tiles:
        if (start_x, start_y) in checked_tiles: continue

        current_block_size = 0
        block_q = deque([(start_x, start_y)])
        block_visited = {(start_x, start_y)}
        checked_tiles.add((start_x, start_y))

        while block_q:
            bx, by = block_q.popleft()
            current_block_size += 1
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = bx + dx, by + dy
                if (nx, ny) in reachable_unseen_tiles and (nx, ny) not in block_visited:
                    block_visited.add((nx, ny))
                    checked_tiles.add((nx, ny))
                    block_q.append((nx, ny))

        if current_block_size > max_block_size:
            max_block_size = current_block_size
            best_target = (start_x, start_y)

    if best_target:
        print(json.dumps({'target_x': best_target[0], 'target_y': best_target[1], 'reasoning': f'Targeting the largest contiguous block of {max_block_size} unseen tiles.'}))
    else:
        print(json.dumps({'target_x': None, 'target_y': None, 'reasoning': 'Could not determine a strategic target from reachable unseen tiles.'}))

plan_step()
