import xml.etree.ElementTree as ET
import heapq
import json

def pathfinder(map_xml_string, target_x, target_y, party_data_str):
    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))
    grid = [[None for _ in range(width + 1)] for _ in range(height + 1)]
    player_pos = None

    party_data = json.loads(party_data_str)

    def has_hm(hm_name):
        for pokemon in party_data:
            if not pokemon.get('is_fainted', False):
                for move in pokemon.get('moves', []):
                    if move.get('name') == hm_name:
                        return True
        return False

    can_surf = has_hm('SURF')
    can_cut = has_hm('CUT')
    can_strength = has_hm('STRENGTH')

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            has_object = tile_elem.find('Object') is not None
            has_player = tile_elem.find('Player') is not None
            is_impassable = tile_type in ['impassable', 'unknown'] or (has_object and (x,y) != player_pos) 

            if tile_type == 'cuttable' and not can_cut:
                is_impassable = True
            if tile_type == 'water' and not can_surf:
                is_impassable = True

            grid[y][x] = {'type': tile_type, 'impassable': is_impassable}
            if has_player:
                player_pos = (x, y)

    if not player_pos:
        return json.dumps({'success': False, 'error': 'Player position not found.'})

    start_node = (player_pos[0], player_pos[1])
    target_node = (target_x, target_y)

    def get_neighbors(x, y):
        neighbors = []
        for dx, dy, move in [(0, 1, 'Down'), (0, -1, 'Up'), (1, 0, 'Right'), (-1, 0, 'Left')]:
            nx, ny = x + dx, y + dy
            if 1 <= nx <= width and 1 <= ny <= height:
                neighbor_tile = grid[ny][nx]
                current_tile = grid[y][x]

                if neighbor_tile['type'] == 'ledge' and dy == 1: # Moving down onto a ledge
                    if 1 <= ny + 1 <= height and not grid[ny + 1][nx]['impassable']:
                         neighbors.append(((nx, ny + 1), move, 1))
                    continue
                elif current_tile['type'] == 'ledge': # On a ledge
                     if dx != 0: continue # Cannot move sideways on a ledge

                if neighbor_tile['type'] == 'steps' and current_tile['type'] in ['ground', 'elevated_ground']:
                    neighbors.append(((nx, ny), move, 1))
                elif current_tile['type'] == 'steps' and neighbor_tile['type'] in ['ground', 'elevated_ground']:
                    neighbors.append(((nx, ny), move, 1))
                elif not neighbor_tile['impassable']:
                    neighbors.append(((nx, ny), move, 1))
        return neighbors

    open_set = [(0, start_node)]
    came_from = {}
    g_score = { (x,y): float('inf') for y in range(height + 1) for x in range(width + 1) }
    g_score[start_node] = 0
    f_score = { (x,y): float('inf') for y in range(height + 1) for x in range(width + 1) }
    f_score[start_node] = abs(start_node[0] - target_node[0]) + abs(start_node[1] - target_node[1])

    while open_set:
        _, current = heapq.heappop(open_set)

        if current == target_node:
            path = []
            while current in came_from:
                path.append(current)
                current = came_from[current]
            path.append(start_node)
            return json.dumps({'success': True, 'path': path[::-1]})

        for neighbor_pos, move, cost in get_neighbors(current[0], current[1]):
            tentative_g_score = g_score[current] + cost
            if tentative_g_score < g_score[neighbor_pos]:
                came_from[neighbor_pos] = current
                g_score[neighbor_pos] = tentative_g_score
                f_score[neighbor_pos] = g_score[neighbor_pos] + abs(neighbor_pos[0] - target_node[0]) + abs(neighbor_pos[1] - target_node[1])
                heapq.heappush(open_set, (f_score[neighbor_pos], neighbor_pos))

    return json.dumps({'success': False, 'error': 'No path found.'})

print(pathfinder(map_xml_string, int(input_data['target_x']), int(input_data['target_y']), input_data['party_data']))

