import json
import xml.etree.ElementTree as ET

try:
    object_name_to_find = input_data['object_name']
    root = ET.fromstring(map_xml_string)

    found_objects = []

    for r in root.findall('Row'):
        for t in r.findall('Tile'):
            for obj in t.findall('Object'):
                id_name = obj.get('id-name', '')
                event_name = obj.get('name', '')

                if object_name_to_find.lower() in id_name.lower() or object_name_to_find.lower() in event_name.lower():
                    found_objects.append({
                        'id-name': id_name,
                        'name': event_name,
                        'x': int(t.get('id')),
                        'y': int(r.get('id'))
                    })

    if found_objects:
        print(json.dumps({'found': True, 'objects': found_objects}))
    else:
        print(json.dumps({'found': False, 'message': f"Object '{object_name_to_find}' not found on the current map."}))

except Exception as e:
    print(json.dumps({'found': False, 'error': str(e)}))

pathfinder.python_script:
import json
import xml.etree.ElementTree as ET
from collections import deque

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    ignorable_types_str = input_data.get('ignorable_impassable_types', '')
    ignorable_types = [t.strip() for t in ignorable_types_str.split(',') if t.strip()] if ignorable_types_str else []

    root = ET.fromstring(map_xml_string)
    width = int(root.get('width'))
    height = int(root.get('height'))

    grid = {}
    for r in root.findall('Row'):
        y = int(r.get('id'))
        for t in r.findall('Tile'):
            x = int(t.get('id'))
            has_impassable_object = False
            obj_node = t.find('Object')
            if obj_node is not None and obj_node.get('id-name') != 'Pikachu':
                has_impassable_object = True
            grid[(x, y)] = {
                'type': t.get('type'),
                'has_object': has_impassable_object
            }

    impassable_set = {'impassable', 'unknown', 'water', 'closed_gate', 'impassable_fence', 'cuttable'}
    for t in ignorable_types:
        if t in impassable_set:
            impassable_set.remove(t)

    def is_tile_impassable(x, y):
        tile_data = grid.get((x, y))
        if not tile_data:
            return True
        return tile_data['has_object'] or tile_data['type'] in impassable_set

    if is_tile_impassable(end_x, end_y):
        found_new_target = False
        for dx, dy in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
            nx, ny = end_x + dx, end_y + dy
            if not is_tile_impassable(nx, ny):
                end_x, end_y = nx, ny
                found_new_target = True
                break
        if not found_new_target:
            print(json.dumps({'path_found': False, 'error': 'No traversable tile adjacent to destination.'}))
            exit()

    queue = deque([([ (start_x, start_y) ])])
    visited = {(start_x, start_y)}

    while queue:
        path = queue.popleft()
        cx, cy = path[-1]

        if (cx, cy) == (end_x, end_y):
            json_path = [{'x': p[0], 'y': p[1]} for p in path]
            print(json.dumps({'path_found': True, 'path': json_path}))
            exit()

        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            nx, ny = cx + dx, cy + dy

            if not (1 <= nx <= width and 1 <= ny <= height) or (nx, ny) in visited:
                continue

            if is_tile_impassable(nx, ny):
                continue

            current_tile_data = grid.get((cx, cy))
            if current_tile_data['type'] == 'ledge' and (dy != 1 or dx != 0):
                continue

            if grid.get((nx,ny))['type'] == 'ledge' and dy == -1:
                continue

            visited.add((nx, ny))
            new_path = path + [(nx, ny)]
            queue.append(new_path)

    print(json.dumps({'path_found': False, 'path': []}))

except Exception as e:
    print(json.dumps({'path_found': False, 'error': str(e)}))

wkg_checker.python_script:
import json

def find_node_id(nodes, map_id, x, y):
    for node in nodes:
        if node.get('map_id') == map_id and node.get('coordinates', {}).get('x') == x and node.get('coordinates', {}).get('y') == y:
            return node.get('id')
    return None

try:
    wkg = json.loads(world_knowledge_graph_json_string)
    nodes = wkg.get('nodes', [])
    edges = wkg.get('edges', [])

    element_type = input_data['element_type']
    map_id = input_data['map_id']
    x = int(input_data['x'])
    y = int(input_data['y'])

    found = False

    if element_type == 'node':
        if find_node_id(nodes, map_id, x, y) is not None:
            found = True

    elif element_type == 'edge':
        to_map_id = input_data.get('to_map_id')
        to_x = int(input_data.get('to_x'))
        to_y = int(input_data.get('to_y'))

        source_node_id = find_node_id(nodes, map_id, x, y)
        dest_node_id = find_node_id(nodes, to_map_id, to_x, to_y)

        if source_node_id and dest_node_id:
            for edge in edges:
                s_id = edge.get('source_node_id')
                d_id = edge.get('destination_node_id')

                is_direct_match = (s_id == source_node_id and d_id == dest_node_id)
                is_reverse_match = (not edge.get('is_one_way', False) and s_id == dest_node_id and d_id == source_node_id)

                if is_direct_match or is_reverse_match:
                    found = True
                    break

    print(json.dumps({'exists': found}))

except Exception as e:
    print(json.dumps({'exists': False, 'error': str(e)}))

wkg_inspector.python_script:
import json
wkg = json.loads(world_knowledge_graph_json_string)
nodes = wkg.get('nodes', [])
edges = wkg.get('edges', [])
target_map_id = input_data['map_id']
connections = {'incoming': [], 'outgoing': []}
def get_node_details(node_id):
    for node in nodes:
        if node.get('id') == node_id:
            return {'name': node.get('name'), 'map_id': node.get('map_id'), 'coordinates': node.get('coordinates'), 'tags': node.get('tags', [])}
    return None
for edge in edges:
    source_node_id = edge.get('source_node_id')
    dest_node_id = edge.get('destination_node_id')
    source_node = get_node_details(source_node_id)
    dest_node = get_node_details(dest_node_id)
    if not source_node or not dest_node:
        continue
    connection_details = {'type': edge.get('connection_type'), 'source': source_node, 'destination': dest_node, 'is_one_way': edge.get('is_one_way', False)}
    if source_node['map_id'] == target_map_id and dest_node['map_id'] != target_map_id:
        connections['outgoing'].append(connection_details)
    elif dest_node['map_id'] == target_map_id and source_node['map_id'] != target_map_id:
        connections['incoming'].append(connection_details)
print(json.dumps(connections, indent=2))
