import json
import xml.etree.ElementTree as ET

def find_path(target_x, target_y, map_xml, start_x, start_y):
    root = ET.fromstring(map_xml)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            grid[(x, y)] = tile

    start_tile = grid.get((start_x, start_y))
    if start_tile is None:
        return None

    start_type = start_tile.attrib['type']
    # Determine medium based on starting tile. Assume unseen tiles match the medium.
    start_medium = "WATER" if start_type == 'WATER' else "LAND"

    queue = [(start_x, start_y, [])]
    visited = {(start_x, start_y)}

    while queue:
        cx, cy, path = queue.pop(0)

        if cx == target_x and cy == target_y:
            return path

        for dx, dy, btn in [(0, -1, "Up"), (0, 1, "Down"), (-1, 0, "Left"), (1, 0, "Right")]:
            nx, ny = cx + dx, cy + dy
            if 0 <= nx < width and 0 <= ny < height:
                if (nx, ny) not in visited:
                    tile = grid.get((nx, ny))
                    t_type = tile.attrib['type'] if tile is not None else "unseen"

                    passable = False
                    if start_medium == "WATER":
                        # Water navigation: can move on WATER, unseen, or land tiles (to land).
                        # BUOY is impassable.
                        if t_type in ["WATER", "unseen", "FLOOR", "DOOR"]:
                            passable = True
                        if t_type == "BUOY":
                            passable = False
                    else:
                        # Land navigation: can move on FLOOR, DOOR, unseen, or ledge tiles.
                        # WATER is impassable.
                        if t_type in ["FLOOR", "DOOR", "unseen"]:
                            passable = True
                        elif t_type == "LEDGE_HOP_DOWN":
                            if dy == 1: # Can only hop down (South)
                                passable = True
                        elif t_type == "FLOOR_UP_WALL":
                            if dy != -1: # Cannot move Up (North) through a ledge face
                                passable = True

                    # Collision with objects
                    if tile is not None and tile.find('Object') is not None:
                        # Only block if it's not the target tile or if it's an NPC/Boulder
                        # For simplicity, treat all objects as walls.
                        passable = False

                    if passable:
                        visited.add((nx, ny))
                        queue.append((nx, ny, path + [btn]))
    return None

