import json
import xml.etree.ElementTree as ET
from collections import deque

def find_path(target_x, target_y, map_xml):
    root = ET.fromstring(map_xml)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}
    start_x, start_y = None, None
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            grid[(x, y)] = tile
            if tile.find('Player') is not None:
                start_x, start_y = x, y

    if start_x is None: return None

    start_tile = grid.get((start_x, start_y))
    if start_tile is None: return None
    start_type = start_tile.attrib['type']
    is_start_water = (start_type == 'WATER')

    queue = deque([(start_x, start_y, is_start_water, [{"x": start_x, "y": start_y}])])
    visited = {(start_x, start_y, is_start_water)}

    while queue:
        cx, cy, surfing, path = queue.popleft()

        if cx == target_x and cy == target_y:
            return path

        for dx, dy in [(0, -1), (0, 1), (-1, 0), (1, 0)]:
            nx, ny = cx + dx, cy + dy
            if 0 <= nx < width and 0 <= ny < height:
                tile = grid.get((nx, ny))
                t_type = tile.attrib['type'] if tile is not None else "unseen"

                passable = False
                n_surfing = surfing

                if surfing:
                    if t_type in ["WATER", "unseen"]:
                        passable = True
                    elif t_type in ["FLOOR", "DOOR"]:
                        passable = True
                        n_surfing = False
                    elif t_type == "BUOY":
                        passable = False
                else:
                    if t_type in ["FLOOR", "DOOR", "unseen"]:
                        passable = True
                    elif t_type == "LEDGE_HOP_DOWN":
                        if dy == 1: passable = True
                    elif t_type == "FLOOR_UP_WALL":
                        if dy != -1: passable = True

                if tile is not None and (tile.find('Object') is not None or tile.find('Boulder') is not None):
                    if (nx, ny) != (target_x, target_y):
                        passable = False

                if passable:
                    if (nx, ny, n_surfing) not in visited:
                        visited.add((nx, ny, n_surfing))
                        queue.append((nx, ny, n_surfing, path + [{"x": nx, "y": ny}]))
    return None

