import xml.etree.ElementTree as ET
from collections import deque
import json

# Extract arguments from input_data
start_x = input_data['start_x']
start_y = input_data['start_y']
target_x = input_data['target_x']
target_y = input_data['target_y']
mode = input_data.get('mode', 'walking')

def find_path(start_x, start_y, target_x, target_y, mode="walking"):
    try:
        root = ET.fromstring(map_xml_string)
    except Exception as e:
        print(f"Error parsing XML: {e}")
        return []

    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            t_type = tile.attrib.get('type', 'unseen')
            grid[(x, y)] = t_type

    queue = deque([(start_x, start_y, [])])
    visited = set([(start_x, start_y)])

    directions = [
        (0, -1, "Up"),
        (0, 1, "Down"),
        (-1, 0, "Left"),
        (1, 0, "Right")
    ]

    while queue:
        cx, cy, path = queue.popleft()

        if cx == target_x and cy == target_y:
            print(json.dumps(path))
            return

        for dx, dy, btn in directions:
            nx, ny = cx + dx, cy + dy

            if (nx, ny) in visited:
                continue

            if not (0 <= nx < width and 0 <= ny < height):
                continue

            tile_type = grid.get((nx, ny), 'unseen')
            is_passable = False

            if mode == "walking":
                # Treat unseen as walkable to encourage exploration
                if tile_type in ["FLOOR", "WARP_CARPET_DOWN", "unseen", "GRASS", "DOOR", "STAIRS"]: 
                    is_passable = True
                # CRITIQUE FIX: Treat FLOOR_UP_WALL as wall to avoid ledge traps
                elif tile_type == "FLOOR_UP_WALL":
                    is_passable = False
                else:
                    is_passable = False
            elif mode == "surfing":
                if tile_type in ["WATER", "WATERFALL"]:
                    is_passable = True
                else:
                    is_passable = False

            if is_passable:
                visited.add((nx, ny))
                queue.append((nx, ny, path + [btn]))

    print(json.dumps([]))

find_path(start_x, start_y, target_x, target_y, mode)
