import json
import xml.etree.ElementTree as ET
from collections import deque

def get_reachable_unseen_tiles(map_xml):
    root = ET.fromstring(map_xml)
    width = int(root.get('width'))
    height = int(root.get('height'))

    # Map layout
    grid = {}
    player_pos = None
    for row in root.findall('Row'):
        y = int(row.get('id'))
        for tile in row.findall('Tile'):
            x = int(tile.get('id'))
            tile_type = tile.get('type')
            is_seen = tile.get('seen') == 'true'
            grid[(x, y)] = {'type': tile_type, 'seen': is_seen, 'is_warp': tile.get('is-warp') == 'true'}
            if tile.find('Player') is not None:
                player_pos = (x, y)

    if not player_pos:
        return []

    # BFS to find reachable seen tiles
    reachable_seen = set()
    queue = deque([player_pos])
    visited = {player_pos}

    # Collision types that block movement
    blocking_types = ['WALL', 'COUNTER', 'FLOOR_UP_WALL', 'VOID', 'WATER']

    while queue:
        curr_x, curr_y = queue.popleft()
        reachable_seen.add((curr_x, curr_y))

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = curr_x + dx, curr_y + dy
            if (nx, ny) in grid and (nx, ny) not in visited:
                tile = grid[(nx, ny)]
                # Basic collision check
                if tile['seen'] and tile['type'] not in blocking_types:
                    visited.add((nx, ny))
                    queue.append((nx, ny))

    # Find unseen tiles adjacent to reachable seen tiles
    unseen_reachable = []
    seen_unseen = set()
    for x, y in reachable_seen:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if (nx, ny) in grid and not grid[(nx, ny)]['seen'] and (nx, ny) not in seen_unseen:
                unseen_reachable.append({'x': nx, 'y': ny})
                seen_unseen.add((nx, ny))

    return unseen_reachable

