import xml.etree.ElementTree as ET

def pathfinder(input_data):
    try:
        root = ET.fromstring(map_xml_string)
        map_width = int(root.get('width'))
        map_height = int(root.get('height'))
    except Exception as e:
        print(f'Error during initial setup: {e}')
        return

    player_pos = None
    for row_elem in root.findall('Row'):
        for tile_elem in row_elem.findall('Tile'):
            if tile_elem.get('has-player') == 'true':
                player_pos = (int(tile_elem.get('id')), int(row_elem.get('id')))
                break
        if player_pos:
            break

    grid = [[' ' for _ in range(map_width + 1)] for _ in range(map_height + 1)]

    for row_elem in root.findall('Row'):
        y = int(row_elem.get('id'))
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.get('id'))
            tile_type = tile_elem.get('type')
            is_pikachu = tile_elem.find("./Object[@id-name='Pikachu']") is not None
            has_blocking_object = tile_elem.get('has-object') == 'true' and not is_pikachu
            is_impassable = False
            impassable_types = ['impassable', 'boulder_barrier', 'closed_gate']
            if tile_type in impassable_types: is_impassable = True
            elif has_blocking_object: is_impassable = True
            elif tile_type == 'water': is_impassable = True
            elif tile_type == 'gate_offscreen':
                for marker_elem in tile_elem.findall('Marker'):
                    if marker_elem.text is not None and 'Closed' in marker_elem.text:
                        is_impassable = True
                        break
            if is_impassable:
                grid[y][x] = 'X'
            elif tile_type == 'ledge':
                grid[y][x] = 'L'
            else:
                grid[y][x] = '.'

    if player_pos:
        grid[player_pos[1]][player_pos[0]] = 'P'

    print('--- Grid Visualization ---')
    for r in range(1, map_height + 1):
        print(''.join(grid[r][1:]))
    print('--- End Visualization ---')

pathfinder(input_data)
