import xml.etree.ElementTree as ET
import json
import heapq

def heuristic(a, b):
    return abs(a[0] - b[0]) + abs(a[1] - b[1])

def a_star_search(grid, objects_grid, start, goal, width, height, impassable_tiles):
    goal_type = grid[goal[1]][goal[0]]
    if goal_type not in impassable_tiles and objects_grid[goal[1]][goal[0]]:
        search_targets = {goal}
    else:
        search_targets = set()
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (goal[0] + dx, goal[1] + dy)
            if 0 <= neighbor[0] < width and 0 <= neighbor[1] < height:
                neighbor_type = grid[neighbor[1]][neighbor[0]]
                if neighbor_type not in impassable_tiles and objects_grid[neighbor[1]][neighbor[0]]:
                    search_targets.add(neighbor)
    if not search_targets:
        return None

    close_set = set()
    came_from = {}
    gscore = {start: 0}
    fscore = {start: min(heuristic(start, t) for t in search_targets)}
    oheap = []
    heapq.heappush(oheap, (fscore[start], start))

    while oheap:
        current = heapq.heappop(oheap)[1]
        if current in search_targets:
            path = []
            temp = current
            while temp in came_from:
                path.append(temp)
                temp = came_from[temp]
            path.append(start)
            path.reverse()
            return path

        close_set.add(current)
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)
            if not (0 <= neighbor[0] < width and 0 <= neighbor[1] < height):
                continue
            neighbor_type = grid[neighbor[1]][neighbor[0]]
            if neighbor_type in impassable_tiles or not objects_grid[neighbor[1]][neighbor[0]]:
                continue
            tentative_g_score = gscore[current] + 1
            if tentative_g_score < gscore.get(neighbor, float('inf')):
                came_from[neighbor] = current
                gscore[neighbor] = tentative_g_score
                fscore[neighbor] = tentative_g_score + min(heuristic(neighbor, t) for t in search_targets)
                if neighbor not in close_set:
                    heapq.heappush(oheap, (fscore[neighbor], neighbor))
    return None

def parse_map(xml_string):
    root = ET.fromstring(xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])
    grid = [['' for _ in range(width)] for _ in range(height)]
    objects_grid = [[True for _ in range(width)] for _ in range(height)]
    player_pos = None
    for y, row_elem in enumerate(root.findall('Row')):
        for x, tile_elem in enumerate(row_elem.findall('Tile')):
            grid[y][x] = tile_elem.attrib.get('type')
            if tile_elem.find('Object') is not None or tile_elem.attrib.get('has-object') == 'true':
                objects_grid[y][x] = False
            if tile_elem.find('Player') is not None:
                player_pos = (x, y)
    return grid, objects_grid, width, height, player_pos

grid, objects_grid, width, height, start_node = parse_map(map_xml_string)
if not start_node:
    print(json.dumps([]))
else:
    min_x, min_y = int(input_data['min_x']), int(input_data['min_y'])
    max_x, max_y = int(input_data['max_x']), int(input_data['max_y'])
    impassable_tiles = ['WALL', 'WINDOW', 'CUT_TREE', 'SIGN', 'BOOKSHELF', 'BLACKBOARD', 'MART_SHELF', 'BUOY', 'TV', 'TOWN_MAP', 'BIRD', 'HEADBUTT_TREE', 'FRUIT_TREE', 'COMPUTER', 'PRINTER', 'VOID', 'WATER', 'CAVE', 'COUNTER']

    target_tiles = set()
    for y in range(min_y, max_y + 1):
        for x in range(min_x, max_x + 1):
            if 0 <= y < height and 0 <= x < width and grid[y][x] not in impassable_tiles and objects_grid[y][x]:
                target_tiles.add((x, y))

    full_path = [start_node]
    current_pos = start_node
    target_tiles.discard(current_pos)

    while target_tiles:
        sorted_targets = sorted(list(target_tiles), key=lambda t: heuristic(current_pos, t))
        path_found_for_any_target = False
        for next_target in sorted_targets:
            path_segment = a_star_search(grid, objects_grid, current_pos, next_target, width, height, impassable_tiles)
            if path_segment:
                full_path.extend(path_segment[1:])
                current_pos = full_path[-1]
                for tile in path_segment:
                    target_tiles.discard(tile)
                path_found_for_any_target = True
                break
        if not path_found_for_any_target:
            break

    output_path = [{'x': x, 'y': y} for x, y in full_path]
    print(json.dumps(output_path))
