import xml.etree.ElementTree as ET
import heapq

def to_buttons(path):
    buttons = []
    for i in range(len(path) - 1):
        current_pos = path[i]
        next_pos = path[i+1]
        if next_pos[0] > current_pos[0]:
            buttons.append("Right")
        elif next_pos[0] < current_pos[0]:
            buttons.append("Left")
        elif next_pos[1] > current_pos[1]:
            buttons.append("Down")
        elif next_pos[1] < current_pos[1]:
            buttons.append("Up")
    return buttons

try:
    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    traversable_tiles = {tile.strip() for tile in input_data['traversable_tiles'].split(',')}
    print(f'DEBUG: Traversable tiles set: {traversable_tiles}')

    root = ET.fromstring(map_xml_string)
    width = int(root.attrib['width'])
    height = int(root.attrib['height'])

    tile_data = {}
    for row_elem in root.findall('Row'):
        y = int(row_elem.attrib['id'])
        for tile_elem in row_elem.findall('Tile'):
            x = int(tile_elem.attrib['id'])
            tile_type = tile_elem.attrib['type']
            has_object = tile_elem.attrib.get('has-object') == 'true'
            tile_data[(x, y)] = {'type': tile_type, 'has_object': has_object}

    start_node = (start_x, start_y)
    end_node = (end_x, end_y)

    end_node_info = tile_data.get(end_node, {'type': 'WALL', 'has_object': True})
    if end_node_info['type'] not in traversable_tiles or end_node_info['has_object']:
        potential_ends = []
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            adj_node = (end_node[0] + dx, end_node[1] + dy)
            if adj_node in tile_data and tile_data[adj_node]['type'] in traversable_tiles and not tile_data[adj_node]['has_object']:
                heapq.heappush(potential_ends, (abs(adj_node[0] - start_node[0]) + abs(adj_node[1] - start_node[1]), adj_node))
        if not potential_ends:
            print("No traversable tile adjacent to destination.")
            exit()
        _, end_node = heapq.heappop(potential_ends)

    open_set = []
    heapq.heappush(open_set, (0, start_node))
    came_from = {}
    g_score = {start_node: 0}
    f_score = {start_node: abs(start_node[0] - end_node[0]) + abs(start_node[1] - end_node[1])}

    path_found = False
    while open_set:
        _, current = heapq.heappop(open_set)

        if current == end_node:
            path = []
            temp = current
            while temp in came_from:
                path.append(temp)
                temp = came_from[temp]
            path.append(start_node)
            path.reverse()
            buttons = to_buttons(path)
            print(f'Path found: {buttons}')
            path_found = True
            break

        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            neighbor = (current[0] + dx, current[1] + dy)

            if not (1 <= neighbor[0] <= width and 1 <= neighbor[1] <= height):
                continue

            neighbor_info = tile_data.get(neighbor, {'type': 'WALL', 'has_object': False})
            current_info = tile_data.get(current, {'type': 'WALL', 'has_object': False})

            if neighbor_info['type'] not in traversable_tiles or neighbor_info['has_object']:
                continue

            if current_info['type'] == 'LEDGE_HOP_RIGHT' and dx == -1: continue
            if current_info['type'] == 'LEDGE_HOP_LEFT' and dx == 1: continue
            if current_info['type'] == 'LEDGE_HOP_DOWN' and dy == -1: continue
            if neighbor_info['type'] == 'LEDGE_HOP_RIGHT' and dx != 1: continue
            if neighbor_info['type'] == 'LEDGE_HOP_LEFT' and dx != -1: continue
            if neighbor_info['type'] == 'LEDGE_HOP_DOWN' and dy != 1: continue

            tentative_g_score = g_score.get(current, float('inf')) + 1
            if tentative_g_score < g_score.get(neighbor, float('inf')):
                came_from[neighbor] = current
                g_score[neighbor] = tentative_g_score
                f_score[neighbor] = tentative_g_score + abs(neighbor[0] - end_node[0]) + abs(neighbor[1] - end_node[1])
                if neighbor not in [i[1] for i in open_set]:
                    heapq.heappush(open_set, (f_score[neighbor], neighbor))

    if not path_found:
        print("No path found.")
except Exception as e:
    print(f"An error occurred: {e}")
