import xml.etree.ElementTree as ET
import json
from collections import deque

def solve():
    root = ET.fromstring(map_xml_string)

    start_x = int(input_data['start_x'])
    start_y = int(input_data['start_y'])
    end_x = int(input_data['end_x'])
    end_y = int(input_data['end_y'])
    avoid_warps = input_data.get('avoid_warps', True)

    grid = {}
    for row in root.findall('Row'):
        y = int(row.attrib['id'])
        for tile in row.findall('Tile'):
            x = int(tile.attrib['id'])
            grid[(x, y)] = {
                'type': tile.attrib['type'],
                'has_object': tile.attrib.get('has-object') == 'true'
            }

    # 'unseen' is NOT impassable to allow exploration.
    impassable_base = {'WALL', 'FLOOR_UP_WALL', 'WATER', 'PIT', 'VOID'}
    if avoid_warps:
        impassable_base.add('CAVE')
        impassable_base.add('WARP')

    queue = deque([(start_x, start_y, [])])
    visited = {(start_x, start_y)}

    while queue:
        x, y, path = queue.popleft()

        if x == end_x and y == end_y:
            print(json.dumps(path))
            return

        for dx, dy, btn in [(0, -1, 'Up'), (0, 1, 'Down'), (-1, 0, 'Left'), (1, 0, 'Right')]:
            nx, ny = x + dx, y + dy

            if (nx, ny) in grid and (nx, ny) not in visited:
                tile_info = grid[(nx, ny)]
                tile_type = tile_info['type']
                has_object = tile_info['has_object']

                can_pass = True
                if nx == end_x and ny == end_y:
                    can_pass = True
                elif tile_type in impassable_base or has_object:
                    can_pass = False

                # Ledge Logic: A ledge tile can only be entered from the direction it hops from.
                if tile_type == 'LEDGE_HOP_DOWN' and dy != 1:
                    can_pass = False
                if tile_type == 'LEDGE_HOP_UP' and dy != -1:
                    can_pass = False
                if tile_type == 'LEDGE_HOP_LEFT' and dx != -1:
                    can_pass = False
                if tile_type == 'LEDGE_HOP_RIGHT' and dx != 1:
                    can_pass = False

                if can_pass:
                    if tile_type.startswith('LEDGE_HOP_'):
                        nnx, nny = nx + dx, ny + dy
                        if (nnx, nny) in grid and (nnx, nny) not in visited:
                            nn_info = grid[(nnx, nny)]
                            nn_type = nn_info['type']
                            nn_has_obj = nn_info['has_object']
                            if (nn_type not in impassable_base and not nn_has_obj) or (nnx == end_x and nny == end_y):
                                visited.add((nnx, nny))
                                queue.append((nnx, nny, path + [btn]))
                    else:
                        visited.add((nx, ny))
                        queue.append((nx, ny, path + [btn]))

    print(json.dumps([]))

solve()

