find_reachable_unseen_tiles_v2.python_script:
import xml.etree.ElementTree as ET
import json
from collections import deque

def solve():
    root = ET.fromstring(map_xml_string)
    start_x, start_y = None, None
    grid = {}
    for row in root.findall("Row"):
        y = int(row.attrib['id'])
        for tile in row.findall("Tile"):
            x = int(tile.attrib['id'])
            grid[(x, y)] = tile
            if tile.find("Player") is not None:
                start_x, start_y = x, y

    if start_x is None: return []

    reachable = set()
    queue = deque([(start_x, start_y)])
    reachable.add((start_x, start_y))

    while queue:
        x, y = queue.popleft()
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if (nx, ny) in grid:
                tile = grid[(nx, ny)]
                is_walkable = tile.attrib['seen'] == 'true' and tile.attrib['type'] not in ['WALL', 'unseen']
                has_object = tile.find("Object") is not None

                if is_walkable and not has_object and (nx, ny) not in reachable:
                    reachable.add((nx, ny))
                    queue.append((nx, ny))

    unseen_reachable = []
    for x, y in reachable:
        for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            nx, ny = x + dx, y + dy
            if (nx, ny) in grid:
                tile = grid[(nx, ny)]
                if tile.attrib['seen'] == 'false':
                    unseen_reachable.append({"x": nx, "y": ny})

    unique = []
    seen_coords = set()
    for item in unseen_reachable:
        coord = (item["x"], item["y"])
        if coord not in seen_coords:
            unique.append(item)
            seen_coords.add(coord)
    return unique

print(json.dumps(solve()))
